'with sd as (',
'    select	s.ID as "ID",',
'           s.security_group_id,',
'           s.username session_owner,',
'           s.created_on,',
'           count(*) item_cache_count,',
'           count(distinct(d.flow_id)) distinct_applications,',
'           s.remote_addr ip_address,',
'           s.idle_timeout_on,',
'           s.life_timeout_on,',
'           s.session_lang,',
'           s.session_territory,',
'           s.session_time_zone,',
'           s.debug_level,',
