'        mt.most_recent_view,',
'        sd.distinct_applications,',
'        sd.idle_timeout_on,',
'        sd.life_timeout_on,',
'        sd.session_lang,',
'        sd.session_territory,',
'        sd.session_time_zone,',
'        sd.debug_level,',
'        sd.trace_mode',
'  from sd, mt',
