,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select /* APEX4350P74a */',
'       the_date,',
'       to_char(the_date,''YYYYMMDD'') passable_date,',
'       to_char(the_date,''Day'') day_of_week,',
'       to_number(to_char(the_date,''D'')) day_of_week_numeric,',
'       to_char(the_date,''Month'') month,',
'       flows applications,',
'       users users,',
'       avg_time,',
'       max_time,',
'       graph pct_of_total,',
'       cached_regions cached_region_views,',
'       cached_pages cached_page_views,',
'       hits page_events,',
'       page_views,',
'       page_posts,',
'       sessions,',
'       ip_addresses,',
'       content_length',
'from (',
'select ',
'  trunc(time_stamp) the_date,',
'  count(1) hits,',
'  sum(decode(page_mode,''A'',1,0)) page_posts,',
'  sum(decode(page_mode,''A'',0,1)) page_views,',
'  count(distinct l.FLOW_ID) flows,',
'  count(distinct l.USERID) users,',
'  count(distinct l.session_id) sessions,',
'  count(distinct l.ip_address) ip_addresses,',
'  avg(l.ELAP) avg_time,',
'  max(l.ELAP) max_time,',
'  ceil(greatest(trunc(100*to_number(count(1)))/nvl(l2.total_hits,1))) graph,',
'  sum(cached_regions) cached_regions,',
'  sum(decode(page_mode,''R'',1,0)) cached_pages,',
'  sum(nvl(content_length,0)) content_length',
'from wwv_flow_activity_log l, ',
'   ( select count(1) total_hits ',
'     from   wwv_flow_activity_log ',
'     where time_stamp >= sysdate - :P74_DATE and  ',
'            security_group_id = :app_security_group_id) l2',
'where security_group_id = :app_security_group_id and',
'          time_stamp >= sysdate - :P74_DATE ',
'group by trunc(time_stamp), to_char(time_stamp,''YYYYMMDD''), l2.total_hits',
') x'))
,p_plug_source_type=>'NATIVE_IR'
