,p_region_template_options=>'#DEFAULT#:a-IRR-region--noOuterBorders'
,p_component_template_options=>'#DEFAULT#'
,p_plug_template=>wwv_flow_imp.id(829445215771671657.4353)
,p_plug_display_sequence=>20
,p_plug_new_grid_row=>false
,p_plug_display_column=>1
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select /* APEX4350P93a */',
'       x.application_id,',
'       x.page_id,',
'       x.report_id,',
'       x.report_type,',
'       x.report_name,',
'       x.owner,',
'       x.userid,',
'       x.workspace,',
'       max(x.time_stamp) maxts',
'from',
'(select l.flow_id application_id,',
'       l.step_id page_id,',
'       l.ir_report_id report_id,',
'       case when r.is_default = ''Y''then ',
'            decode(r.application_user,',
'                   ''APXWS_DEFAULT'',sm.primary_def, ',
'                   ''APXWS_ALTERNATIVE'',sm.alt_def)',
'            when r.is_default = ''N'' and r.status = ''PUBLIC'' then',
'                sm.spublic',
'            when r.is_default = ''N'' and r.status = ''PRIVATE'' then',
'                 sm.private',
'       end report_type, ',
'       case when r.is_default=''Y'' and r.application_user=''APXWS_DEFAULT'' then',
'           wwv_flow_lang.system_message(''SAVED_REPORTS.PRIMARY_REPORT'')',
'       else',
'           r.name',
'       end report_name,',
'       decode(r.application_user,',
'              ''APXWS_DEFAULT'',null,',
'              ''APXWS_ALTERNATIVE'',null,',
'              r.application_user) owner,              ',
'       l.time_stamp,',
'       l.userid,',
'       (select short_name from wwv_flow_companies where provisioning_company_id=l.security_group_id) workspace',
'from wwv_flow_activity_log l,',
'     wwv_flow_worksheets w,',
'     wwv_flow_worksheet_rpts r,',
'     (select wwv_flow_lang.system_message(''SAVED_REPORTS.PRIMARY.DEFAULT'') primary_def,',
'             wwv_flow_lang.system_message(''SAVED_REPORTS.ALTERNATIVE.DEFAULT'') alt_def,             ',
'             wwv_flow_lang.system_message(''SAVED_REPORTS.PRIVATE'') private,',
'             wwv_flow_lang.system_message(''SAVED_REPORTS.PUBLIC'') spublic',
'      from dual) sm     ',
'where w.id = r.worksheet_id',
'and w.flow_id = l.flow_id',
'and w.page_id = l.step_id',
'and l.ir_report_id = r.id',
'and w.security_group_id = :flow_security_group_id',
'and l.ir_report_id is not null) x',
'group by x.application_id,x.page_id,x.report_id,x.report_type,x.report_name,x.owner, x.userid, x.workspace'))
,p_plug_source_type=>'NATIVE_IR'
