,p_validation_sequence=>10
,p_validation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_need_check varchar2(1) := ''N'';',
'begin',
'   if :P17_COLUMN_ID is not null then',
'      for c1 in (',
'         select column_name old_col_name',
'           from wwv_flow_hnt_column_dict',
'          where column_id = :P17_COLUMN_ID',
'            and security_group_id = :flow_security_group_id )',
'      loop',
'         if c1.old_col_name != :P17_COLUMN_NAME then',
'            l_need_check := ''Y'';  -- name change',
'         end if;',
'      end loop;',
'   else l_need_check := ''Y'';  -- new record',
