,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with my_s as (select sid from sys.gv_$mystat where rownum = 1),',
'wait_obj as (select s1.sid,d1.owner||''.''||d1.object_name obj_name,s1.row_wait_obj#,d1.object_id,d1.data_object_id,d1.object_name,d1.owner object_owner,',
'                    d1.object_type',
'               from sys.gv_$session s1,sys.dba_objects d1',
'              where s1.row_wait_obj# = d1.object_id',
'                and s1.wait_time = 0',
'                and s1.wait_class != ''Idle''),',
'ws_schemas as(',
'    select schema ',
'      from wwv_flow_company_schemas',
'     where security_group_id = :flow_security_group_id)',
'select case when (select sid from my_s where rownum = 1) = s.sid then  ',
'                  ''C'' ',
'             when s.status = ''ACTIVE'' then ',
'                  case when sw.seconds_in_wait > 30  ',
'                       then ''L'' ',
'                       else ''A'' ',
'                  end ',
'             when  s.status = ''INACTIVE'' ',
'                       then ''I'' ',
'            else null ',
'        end session_status,',
'       s.sid,s.username,sw.event, decode(sw.wait_time,0,''W'',''C'') waiting,',
'       sw.wait_time, sw.state,sw.seconds_in_wait,s.status,s.module,s.client_info,',
'       case when o.object_type in (''TABLE'',''VIEW'',''INDEX'',''MATERIALIZED VIEW'') then',
'            case when o.object_owner in  (select schema from ws_schemas)',
'                 then ''<a href="''||wwv_flow_page_api.get_url(',
'                                 p_page        => 2000,',
'                                 p_clear_cache => ''RP,2000'',',
'                                 p_items       => ''P2000_OB_INIT_SCHEMA,P2000_OB_INIT_ID'',',
'                                 p_values      => o.object_owner||'',''||o.object_id )||''">''||wwv_flow_escape.html(o.object_name)||''</a>'' ',
'                 else wwv_flow_escape.html(o.object_name) end',
'            else wwv_flow_escape.html(object_name) end object_name,',
'       sw.p1text,sw.p1,sw.p2text,sw.p2,sw.p3text,sw.p3,sw.wait_class,',
'       s.inst_id,',
'       null dummy',
'  from sys.gv_$session s, sys.gv_$session_wait sw, wait_obj o',
