,p_process_name=>'create script'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_max_identifier_length number := wwv_flow_db_version.c_actual_dbms_id_length;',
'    l_script_name           varchar2(255);',
'    l_clob                  clob;',
'    l_file_id               number;',
'begin',
'',
'    l_script_name := ''FK indexes for '' || :P704_OWNER;',
'',
'    l_clob := '''';',
'',
'    -- this must use the dba dictionary views',
'    for r in (',
'        with',
'        /*gather all referential constraints of interest, including their column lists*/',
'        cons_cols as (',
'            select',
'                cons.owner,',
'                cons.table_name,',
'                cons.constraint_name,',
'                listagg(''"''||cols.column_name||''"'', '','') within group (order by cols.position) as columns',
'            from sys.dba_constraints cons',
'            join sys.dba_cons_columns cols',
'                on cols.owner = cons.owner',
'                and cols.constraint_name = cons.constraint_name',
'            where cons.constraint_type = ''R''',
'            and cons.table_name not like ''BIN$%''',
'            and cons.table_name not like ''DR$%''',
'            and cons.table_name not like ''APEX$%''',
'            and cons.owner = :P704_OWNER',
'            group by',
'                cons.owner,',
'                cons.table_name,',
'                cons.constraint_name',
'        ),',
'        /*generate a suggested name for the index*/',
'        cons as (',
'            select',
'                cons_cols.owner,',
'                cons_cols.table_name,',
'                cons_cols.constraint_name,',
'                cons_cols.columns,',
'                replace(''%_IDX'', ''%'',',
'                    rtrim(substr(',
'                        case when cons_cols.constraint_name like ''SYS/_%'' escape ''/''',
'                        then replace(cons_cols.constraint_name,''_FK'','''')',
'                        else cons_cols.table_name || ''_'' || translate(cons_cols.columns,''A,"'',''A'')',
'                        end',
'                        ,1,l_max_identifier_length-length(''%_IDX'')+1)',
'                        ,''_'')',
'                   ) as suggested_index_name        ',
'            from cons_cols',
'        )',
'        /*report any referential integrity constraints that don''t have a matching index (i.e. leading columns are indexed)*/',
'        select',
'            cons.owner,',
'            cons.table_name,',
'            cons.constraint_name,',
'            cons.columns as foreign_key_columns,',
'            suggested_index_name,',
'            ''CREATE INDEX "'' || cons.owner || ''"."'' || cons.suggested_index_name',
'                || ''" ON "'' || cons.owner || ''"."'' || cons.table_name',
'                || ''" ('' || cons.columns || '');'' as suggested_ddl',
'        from cons',
'        left join (',
'            select',
'                table_owner,',
'                table_name,',
'                index_name,',
'                listagg(''"''||column_name||''"'', '','') within group (order by column_position) as columns',
'            from sys.dba_ind_columns',
'            where table_owner = :P704_OWNER',
'            group by',
'                table_owner,',
'                table_name,',
'                index_name',
'            ) ind',
'            on ind.table_owner = cons.owner',
'            and ind.table_name = cons.table_name',
'            and ind.columns like cons.columns || ''%'' /*if the index includes additional columns, it''s still suitable*/',
'        where ind.table_name is null /*only show the ones where a matching index was not found*/',
'        order by',
'            cons.owner,',
'            cons.table_name,',
'            cons.constraint_name',
'    ) loop',
'        l_clob := case when l_clob is not null then',
'                      l_clob',
'                      || wwv_flow.lf',
'                  end',
'            || ''prompt Create index '' || r.suggested_index_name || '' on '' || r.owner || ''.'' || r.table_name',
'            || wwv_flow.lf',
'            || r.suggested_ddl',
'            || wwv_flow.lf;',
'    end loop;',
'',
'    if l_clob is null then',
'        raise_application_error(-20000, ''No indexes were suggested.'');',
'    end if;',
'    ',
'    l_file_id := wwv_flow_id.next_val;',
'',
'    wwv_flow_sw_script.create_script_from_clob (        ',
'        p_script_name => l_script_name,',
'        p_script      => l_clob,',
'        p_file_id     => l_file_id',
'    );',
'    ',
'    :P704_FILE_ID     := l_file_id;',
'    :P704_SCRIPT_NAME := l_script_name;',
'',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_process_error_message=>'#SQLERRM_TEXT#'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(2700768472215607.4503)
,p_process_success_message=>'Skript erstellt: &P704_SCRIPT_NAME.'
