'    end if;',
'  ',
'    -- results',
'    if instr('':''||:P1225_VIEW_MODE||'':'','':RESULTS:'') > 0 then',
'          sys.htp.prn(''<div class="htmldbScriptResult">'');',
'    	wwv_flow_sw_script.print_result(p_id => c1.id);',
'          sys.htp.prn(''</div>'');',
'    end if;',
'  ',
'    -- feedback',
'    if instr('':''||:P1225_VIEW_MODE||'':'','':MSG:'') > 0 then',
'      sys.htp.prn(''<br />'');',
'      sys.htp.prn(wwv_flow_escape.html(c1.msg));',
'    end if;',
'  ',
'    -- rows',
'    /*if instr('':''||:P1225_VIEW_MODE||'':'','':MSG:'') > 0 then',
'      if c1.result_rows is not null then',
'        if c1.result_rows = 1 then ',
'        	 sys.htp.prn(wwv_flow_lang.system_message(',
'        	          ''WWV_FLOW_SW_SCRIPT.RESULT_ROW'',',
'        	          to_char(c1.Result_Rows,''999G999G999G999G999G999G990'')',
'        	          ));                ',
'        else         ',
'           sys.htp.prn(wwv_flow_lang.system_message(',
