,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_job_is_running boolean := false;',
'begin',
'    --',
'    -- Step 1: Determine whether the scheduler job is running',
'    --',
'    for j in (',
'        select 1',
'          from sys.user_scheduler_jobs',
'         where job_name like ''ORACLE_APEX_DTLD$%''',
'           and regexp_substr( comments, ''(\:)(.*?)(\:)'',1,5,''i'',2 ) = ''APEX$DATALOAD_STATUS''||:P3100_LOAD_ID',
'           and state    = ''RUNNING'' ',
'           and rownum   = 1 )',
'    loop',
'        l_job_is_running := true;',
'    end loop;',
'',
'    --',
'    -- Step 2: Determine the current status from the status collection',
'    --',
'    for l_load in (',
'        select c001 as status,',
'               c002 as error_message,',
'               n001 as rows_processed ',
'        from wwv_flow_collections ',
'        where collection_name=''APEX$DATALOAD_STATUS''||:P3100_LOAD_ID)',
'    loop',
'        :P3110_LOAD_STATUS       := l_load.status;',
'        :P3110_LOAD_ERROR        := l_load.error_message;',
'        :P3110_PROCESSED_ROW_CNT := l_load.rows_processed;',
'    end loop;',
'',
'    if :P3110_LOAD_STATUS = ''RUNNING'' and not l_job_is_running then',
'        :P3110_LOAD_STATUS       := ''FAILED'';',
'        :P3110_LOAD_ERROR        := wwv_flow_lang.system_message(''DATA.LOAD.JOB_INTERMITTENTLY_FAILED'');',
'    end if;',
'    ',
'    :P3110_PROCESSED_ROW_CNT_DISP := to_char( :P3110_PROCESSED_ROW_CNT, ''999G999G999G999G999G999G990'' );',
'end;'))
,p_attribute_03=>'P3110_LOAD_STATUS,P3110_PROCESSED_ROW_CNT,P3110_LOAD_ERROR,P3110_PROCESSED_ROW_CNT_DISP'
,p_attribute_04=>'N'
,p_attribute_05=>'PLSQL'
,p_wait_for_result=>'Y'
);
