,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with',
'l_msg as (',
'    select wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.UNMAPPED'') unmapped',
'    from sys.dual)',
', l_columns as (',
'    select ',
'        fc.column_position,    ',
'        case when :P3120_FIRST_LINE = ''HEADERS'' then',
'            fc.column_name',
'        else ',
'            ''COL'' || lpad( rownum,3,''0'')',
'        end as column_name,',
'        case when :P3120_FIRST_LINE = ''HEADERS'' then',
'            ( select tc.column_name ',
'              from sys.dba_tab_columns tc',
'              where tc.owner = :P3100_TABLE_OWNER',
'              and tc.table_name = :P3100_EXISTING_TABLE',
'              and tc.column_name = fc.column_name )',
'        else',
'             null',
'        end as map_column_name, ',
'        fc.data_type, ',
'        fc.format_mask,',
'        fc.detected_group_char group_char,',
'        fc.detected_decimal_char decimal_char',
'    from wwv_flow_collections c,',
'         table( wwv_flow_data_parser.get_columns( c.clob001 ) ) fc',
'    where c.collection_name =''APEX$DATALOAD'' )',
', l_sum_existing_tab_col as (',
'    select sum( length( ''<option value="''|| column_name ||''">''|| column_name ||'' (''|| data_type ||'')</option>'' ) ) total_length',
'    from sys.dba_tab_columns tc',
'    where tc.owner = :P3100_TABLE_OWNER',
'    and tc.table_name = :P3100_EXISTING_TABLE )',
'select',
'    c.column_position,',
'    c.column_name,',
'    c.data_type,',
'    htmldb_item.hidden(',
'        p_idx            => 1,',
'        p_value          => c.column_name)||',
'    case when tc.total_length <= 3900 then',
'        htmldb_item.select_list_from_lov(',
'            p_idx            => 2,',
'            p_value          => m.c002,',
'            p_lov            => ''P3120_TABLE_COLUMNS'',',
'            p_null_value     => null,',
'            p_null_text      => msg.unmapped,         ',
'            p_show_extra     => ''NO'',',
'            p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.MAP_TO_COLUMN'', wwv_flow_escape.html_attribute(c.column_name))||''"'') ',
'    else',
'        -- bug 30464422 fix, show popup if total existing table column option  > 3900 to avoid error',
'        wwv_flow_item.popup_from_lov(',
'            p_idx            => 2,',
'            p_value          => m.c002,',
'            p_lov_name       => ''P3120_TABLE_COLUMNS'',',
'            p_width          => 40,',
'            p_show_null      => ''YES'',',
'            p_null_value     => null,',
'            p_null_text      => msg.unmapped,',
'            p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.MAP_TO_COLUMN'', wwv_flow_escape.html_attribute(c.column_name))||''"'')',
'    end as column_map,',
'    htmldb_item.text(',
'        p_idx            => 3,',
'        p_value          => m.c003,',
'        p_size           => 20,',
'        p_maxLength      => 1000,',
'        p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.FORMAT_MASK'', wwv_flow_escape.html_attribute(c.column_name))||''"'') as format_mask,',
'    htmldb_item.text(',
'        p_idx            => 4,',
'        p_value          => m.c004,',
'        p_size           => 2,',
'        p_maxLength      => 2,',
'        p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.GROUP_SEPARATOR'', wwv_flow_escape.html_attribute(c.column_name))||''"'') as group_char,',
'    htmldb_item.text(',
'        p_idx            => 5,',
'        p_value          => m.c005,',
'        p_size           => 2,',
'        p_maxLength      => 2,',
'        p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.DECIMAL_CHARACTER'', wwv_flow_escape.html_attribute(c.column_name))||''"'') as decimal_char',
'from l_columns c, l_sum_existing_tab_col tc, l_msg msg, wwv_flow_collections m',
'where c.column_name = m.c001 (+)',
'and wwv_flow_collection.collection_member_count( p_collection_name => ''APEX$DATALOAD_COLUMNS'' ) > 0',
'union',
'select',
'    c.column_position,',
'    c.column_name,',
'    c.data_type, ',
'    htmldb_item.hidden(',
'        p_idx            => 1,',
'        p_value          => c.column_name)||',
'    case when tc.total_length <= 3900 then',
'        htmldb_item.select_list_from_lov(',
'            p_idx            => 2,',
'            p_value          => c.map_column_name,',
'            p_lov            => ''P3120_TABLE_COLUMNS'',',
'            p_null_value     => null,',
'            p_null_text      => msg.unmapped,',
'            p_show_extra     => ''NO'',',
'            p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.MAP_TO_COLUMN'', c.column_name )||''"'')',
'    else',
'        -- bug 30464422 fix, show popup if total existing table column option  > 3900 to avoid error ',
'        wwv_flow_item.popup_from_lov(',
'            p_idx            => 2,',
'            p_value          => c.map_column_name,',
'            p_lov_name       => ''P3120_TABLE_COLUMNS'',',
'            p_width          => 40,',
'            p_show_null      => ''YES'',',
'            p_null_value     => null,',
'            p_null_text      => msg.unmapped,',
'            p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.MAP_TO_COLUMN'', c.column_name )||''"'')',
'    end as column_map,',
'    htmldb_item.text(',
'        p_idx            => 3,',
'        p_value          => c.format_mask,',
'        p_size           => 20,',
'        p_maxLength      => 1000,',
'        p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.FORMAT_MASK'', wwv_flow_escape.html_attribute(c.column_name))||''"'') as format_mask,',
'    htmldb_item.text(',
'        p_idx            => 4,',
'        p_value          => c.group_char,',
'        p_size           => 2,',
'        p_maxLength      => 2,',
'        p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.GROUP_SEPARATOR'', wwv_flow_escape.html_attribute(c.column_name))||''"'') as group_char,',
'    htmldb_item.text(',
'        p_idx            => 5,',
'        p_value          => c.decimal_char,',
'        p_size           => 2,',
'        p_maxLength      => 2,',
'        p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.DECIMAL_CHARACTER'', wwv_flow_escape.html_attribute(c.column_name))||''"'') as decimal_char        ',
'from l_columns c, l_msg msg, l_sum_existing_tab_col tc',
'where wwv_flow_collection.collection_member_count( p_collection_name => ''APEX$DATALOAD_COLUMNS'' ) = 0',
'order by 1'))
,p_display_when_condition=>'coalesce( :P3100_LOAD_TO, ''NEW'') = ''EXISTING'''
,p_display_when_cond2=>'PLSQL'
,p_display_condition_type=>'EXPRESSION'
,p_ajax_enabled=>'Y'
