wwv_flow_imp_page.create_page_validation(
 p_id=>wwv_flow_imp.id(81742731063478601.4503)
,p_validation_name=>'check group and decimal char'
,p_validation_sequence=>20
,p_validation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_load_to      varchar2(10);',
'    ',
'    l_group_char   varchar2(4);',
'    l_decimal_char varchar2(4);',
'    l_column_name  varchar2(128);',
'    l_error_msg    varchar2(4000);',
'',
'    function validate_group_decimal_char',
'    return varchar2',
'    is',
'        l_msg varchar2(4000);',
'    begin',
'        -- if group separator defined, decimal character must be defined',
'        if l_group_char is not null then',
'            if l_decimal_char is null then',
'                l_msg := wwv_flow_lang.system_message( ',
'                            ''DATA_LOAD.DECIMAL_CHAR_REQUIRED'',',
'                            l_column_name );',
'            end if;',
'        end if;',
'',
'        -- group and decimal chars cannot be same',
'        if l_group_char = l_decimal_char then',
'            l_msg := wwv_flow_lang.system_message(',
'                         ''DATA_LOAD.SAME_GROUP_DECIMAL_CHARS'',',
'                         l_column_name );',
'        end if;',
'        ',
'        return l_msg;',
'    end validate_group_decimal_char;',
'begin',
'    l_load_to := coalesce( :P3100_LOAD_TO, ''NEW'');',
'',
'    if l_load_to = ''NEW'' then',
'        -- loop through all column postions',
'        for i in 1 .. wwv_flow.g_f06.count',
'        loop',
'            for j in 1 .. wwv_flow.g_f01.count',
'            loop',
'                -- if column checked to load, validate',
'                if wwv_flow.g_f01( j ) = wwv_flow.g_f06( i ) then',
'                    l_column_name  := wwv_flow.g_f02( i );',
'                    l_group_char   := wwv_flow.g_f04( i );',
'                    l_decimal_char := wwv_flow.g_f05( i );',
'',
'                    l_error_msg := validate_group_decimal_char;',
'                    if l_error_msg is not null then',
'                        return l_error_msg;',
'                    end if;',
'',
'                    exit;',
'                end if;',
'            end loop;',
'        end loop;',
'    elsif l_load_to= ''EXISTING'' then',
'        for i in 1 .. wwv_flow.g_f02.count',
'        loop',
'            -- if column mapped, check group and decimal char',
'            if wwv_flow.g_f02(i) is not null then',
'                l_column_name  := wwv_flow.g_f02( i );',
'                l_group_char   := wwv_flow.g_f04( i );',
'                l_decimal_char := wwv_flow.g_f05( i );',
'',
'                l_error_msg := validate_group_decimal_char;',
'                if l_error_msg is not null then',
'                    return l_error_msg;',
'                end if;',
'            end if;',
'        end loop;',
'    end if;',
'end;'))
,p_validation2=>'PLSQL'
,p_validation_type=>'FUNC_BODY_RETURNING_ERR_TEXT'
,p_when_button_pressed=>wwv_flow_imp.id(3942483089766704.4503)
,p_error_display_location=>'INLINE_WITH_FIELD_AND_NOTIFICATION'
);
