'    var lTypeAvro    = "1",',
'        lTypeParquet = "2",',
'        lTypeOther   = "3",',
'        lFileType;',
'    ',
'    function setExternalTableName() {',
'        var lTableName;',
'',
'        if ( lFileType === lTypeAvro ) {',
'            lTableName = pFileName.substr( 0, pFileName.indexOf(".avro"));',
'        } else if ( lFileType === lTypeParquet ) {',
'            lTableName = pFileName.substr( 0, pFileName.indexOf(".parquet"));',
'        } ',
'',
'        if ( lTableName ) {',
'            if ( lTableName.indexOf( "/" ) !== -1 ) {',
'                lTableName = lTableName.substr( lTableName.lastIndexOf( "/" ) + 1 );',
'            }',
'            $s("P3130_EXTERNAL_TABLE_NAME", lTableName.toUpperCase().replace(/[\s-\"\''.,#*+%&\/:;<>=)(]/g, "_") + "_EXT");',
'        }',
'    }',
'',
'    if ( pFileName.substr( -5 ).toLowerCase() === ".avro" ) {',
'        lFileType = lTypeAvro;',
'    } else if ( pFileName.substr( -8 ).toLowerCase() === ".parquet") {',
'        lFileType = lTypeParquet;',
'    } else {',
'        lFileType = lTypeOther;',
'    }',
'    ',
'    if ( $v("P3130_FILE_NAME") === "" ) {',
'        $s( "P3130_FILE_NAME", pFileName );',
'        $s( "P3130_FILE_TYPE", lFileType );',
'        setExternalTableName();',
'    } else {',
'        if ( $v("P3130_FILE_TYPE") === lTypeOther ) {',
'            $s( "P3130_FILE_NAME", pFileName );  ',
'            $s( "P3130_FILE_TYPE", lFileType );',
'            if ( lFileType !== lTypeOther ) {',
'                setExternalTableName();',
'            }',
'        } else {',
'            if ( ( "," + $v("P3130_FILE_NAME") + ",").indexOf( "," + pFileName + "," ) === -1 ) {',
'                $s("P3130_FILE_NAME", $v("P3130_FILE_NAME") + "," + pFileName );',
'            }',
'        }',
'    }',
