 p_id=>wwv_flow_imp.id(7975736219563599.4503)
,p_name=>'Abgelehnte Zeilen in Tabelle &P3140_ERROR_TABLE_NAME!HTML.'
,p_region_name=>'failed_rows'
,p_template=>wwv_flow_imp.id(380771699411878611.4503)
,p_display_sequence=>80
,p_region_template_options=>'#DEFAULT#:a-Region--noPadding'
,p_component_template_options=>'#DEFAULT#:a-Report--inline:a-Report--stretch:a-Report--rowHighlight'
,p_region_attributes=>'style="display: none;"'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'FUNC_BODY_RETURNING_SQL'
,p_function_body_language=>'PLSQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    q              varchar2(32767);',
'    l_column_name  varchar2(255);',
'    l_columns      varchar2(32767);',
'    l_profile_json clob;',
'    l_file_profile wwv_flow_data_parser.t_file_profile;',
'begin',
'    ',
'    select clob001 ',
'    into l_profile_json',
'    from wwv_flow_collections',
'    where collection_name = ''APEX$DATALOAD''',
'    and rownum = 1;',
'',
'    -- query upto 10 columns not including CLOB',
'    for l_file in ( ',
'        select column_position, column_name, data_type ',
'        from table( wwv_flow_data_parser.get_columns(l_profile_json ) )',
'        where rownum <= 10',
'        and data_type != ''CLOB'' )',
'    loop',
'        l_column_name := null;',
'',
'        if :P3141_LOAD_TO = ''EXISTING'' then',
'            for l_existing_table in (',
'                select column_name',
'                from sys.dba_tab_columns',
'                where owner = :P3140_SCHEMA',
'                and table_name = :P3140_EXISTING_TABLE_NAME',
'                and column_name = l_file.column_name )',
'            loop',
'                l_column_name := l_existing_table.column_name;',
'            end loop;',
'            -- if column header not used, columns such as c001 mapped to existing column',
'            if l_column_name is null then',
'                for l_configure in (',
'                    select',
'                        c002 mapped_column',
'                    from wwv_flow_collections',
'                    where collection_name = ''APEX$DATALOAD_COLUMNS''',
'                    and c002 is not null',
'                    and c001 = l_file.column_name )',
'                loop',
'                    l_column_name := l_configure.mapped_column;',
'                end loop;',
'            end if;',
'        else',
'            -- validate file columns against newly created table',
'            -- get only the ones created',
'            for l_table in (',
'                select column_name',
'                from sys.dba_tab_columns',
'                where owner = :P3140_SCHEMA',
'                and table_name = :P3140_NEW_TABLE_NAME',
'                and column_name = l_file.column_name )',
'            loop',
'                l_column_name := l_table.column_name;',
'            end loop;',
'        end if;',
'',
'        if l_column_name is not null then',
'            l_columns := l_columns ||case when l_columns is not null then '','' end || l_column_name;',
'        end if;',
'    end loop;',
'   ',
'    q := ''select ORA_ERR_MESG$ as error_message''|| ',
'         (case when l_columns is not null then '',''|| l_columns end ) ||',
'         '' from ''||wwv_flow_assert.enquote_name( :P3140_ERROR_TABLE_NAME );',
'',
'    return q;',
'end;'))
,p_header=>'Alle abgelehnten Zeilen werden in der Tabelle &P3140_ERROR_TABLE_NAME!HTML. gespeichert. Im Bericht unten werden die ersten 10 Zeilen angezeigt.'
,p_ajax_enabled=>'Y'
