,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_job_is_running number;',
'begin',
'    for l_load in (',
'        select c001 as status,',
'               c002 as error_message,',
'               n001 as rows_processed ',
'        from wwv_flow_collections ',
'        where collection_name=''APEX$DATALOAD_STATUS''||:P3100_LOAD_ID)',
'    loop',
'        :P3141_LOAD_STATUS       := l_load.status;',
'        :P3141_LOAD_ERROR        := l_load.error_message;',
'        :P3141_PROCESSED_ROW_CNT := l_load.rows_processed;',
'    end loop;',
'',
'    -- if the status is "RUNNING", check whether we really have an active scheduler job',
'    if :P3141_LOAD_STATUS = ''RUNNING'' then',
'        begin',
'            select 1',
'              into l_job_is_running',
'              from user_scheduler_jobs',
'             where job_name like ''ORACLE_APEX_DTLD$%''',
'               and regexp_substr( comments, ''(\:)(.*?)(\:)'',1,5,''i'',2 ) = ''APEX$DATALOAD_STATUS''||:P3100_LOAD_ID',
'               and state = ''RUNNING'';',
'        exception ',
'            when no_data_found then',
'                :P3141_LOAD_STATUS       := ''FAILED'';',
'                :P3141_LOAD_ERROR        := wwv_flow_lang.system_message(''DATA.LOAD.JOB_INTERMITTENTLY_FAILED'');',
'        end;',
'    end if;',
'    ',
'    :P3141_PROCESSED_ROW_CNT_DISP := to_char( :P3141_PROCESSED_ROW_CNT, ''999G999G999G999G999G999G990'' );',
'end;'))
,p_attribute_03=>'P3141_LOAD_STATUS,P3141_PROCESSED_ROW_CNT,P3141_LOAD_ERROR,P3141_PROCESSED_ROW_CNT_DISP'
