,p_execute_on_page_init=>'N'
,p_name=>'Calculate good / bad row count'
,p_action=>'NATIVE_EXECUTE_PLSQL_CODE'
,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_error_table_exists boolean := false;',
'    l_context wwv_flow_exec.t_context;',
'    q varchar2(32767);',
'begin',
'    if :P3141_LOAD_STATUS in (''FAILED'', ''FINISHED'' ) then',
'        if :P3141_LOAD_TO = ''NEW'' then',
'            for l_table in (',
'                select table_name',
'                from sys.dba_tables',
'                where table_name = :P3140_NEW_TABLE_NAME',
'                and owner = :P3140_SCHEMA )',
'            loop',
'                :P3141_TABLE_CREATED := ''Y'';',
'            end loop;',
'        end if;',
'        ',
'        for l_table in (',
'            select table_name',
'            from sys.dba_tables',
'            where table_name = :P3140_ERROR_TABLE_NAME',
'            and owner = :P3140_SCHEMA )',
'        loop',
'            l_error_table_exists := true;',
'        end loop;',
'        ',
'        q := ''with'';',
'        q := q||'' success_data as (select count(*) as cnt from ''||',
'             wwv_flow_assert.enquote_name(',
'                 case when :P3141_LOAD_TO = ''NEW'' then',
'                     :P3140_NEW_TABLE_NAME',
'                 when :P3141_LOAD_TO = ''EXISTING'' then',
'                     :P3140_EXISTING_TABLE_NAME',
'                 end )',
'                 || '')'';',
'        if l_error_table_exists then',
'            q := q||'', failed_data as (select count(*) as cnt from ''||wwv_flow_assert.enquote_name( :P3140_ERROR_TABLE_NAME ) || '')'';',
'        end if;',
'        q := q||'' select s.cnt as success_count'';',
'        if l_error_table_exists then',
'            q := q||'', e.cnt as error_count'';',
'        end if;',
'        q:=q||'' from'';',
'        q:=q||'' success_data s'';',
'        if l_error_table_exists then',
'            q:=q||'', failed_data e'';',
'        end if;',
'     ',
'        wwv_flow_exec.open_query_context(',
'            p_context     => l_context,',
'            p_location    => wwv_flow_exec_api.c_location_local_db,',
'            p_query_type  => wwv_flow_exec_api.c_query_type_sql_query,',
'            p_sql_query   => q,',
'            p_parse_as_schema_override => :P3140_SCHEMA );',
'            ',
'        if wwv_flow_exec.next_row( p_context => l_context ) then',
'            :P3141_GOOD_ROW_CNT := wwv_flow_exec.get_number( p_context => l_context, p_column_idx => 1 );',
'            if l_error_table_exists then',
'                :P3141_BAD_ROW_CNT := wwv_flow_exec.get_number( p_context => l_context, p_column_idx => 2 );',
'            else',
'                :P3141_BAD_ROW_CNT := 0;',
'            end if;',
'        end if;',
'        ',
'        if :P3141_LOAD_TO = ''EXISTING'' then',
'            :P3141_GOOD_ROW_CNT := :P3141_PROCESSED_ROW_CNT - :P3141_BAD_ROW_CNT;',
'        end if;',
'        ',
'        :P3141_GOOD_ROW_CNT_DISP := to_char( :P3141_GOOD_ROW_CNT, ''999G999G999G999G999G999G990'' );',
'        :P3141_BAD_ROW_CNT_DISP  := to_char( :P3141_BAD_ROW_CNT,  ''999G999G999G999G999G999G990'' );',
'        ',
'        wwv_flow_exec.close_context( p_context => l_context );',
'    end if;',
'exception ',
'    when others then',
'        wwv_flow_exec.close_context( p_context => l_context );',
'        raise;',
'end;'))
,p_attribute_03=>'P3141_GOOD_ROW_CNT,P3141_BAD_ROW_CNT,P3141_TABLE_CREATED,P3141_TAB_OBJ_ID,P3141_GOOD_ROW_CNT_DISP,P3141_BAD_ROW_CNT_DISP'
,p_attribute_04=>'N'
