'    end if;',
'    ',
'    if l_file_type = wwv_flow_soda_dev.c_file_type_json_collection then',
'        l_row_selector := case when :P3150_AUTO_DETECT_SELECTOR = ''N''  then :P3150_ROW_SELECTOR',
'                               when :P3150_AUTO_DETECT_SELECTOR = ''CR'' then chr( wwv_flow_soda_dev.c_row_selector_chr_jsonlist )',
