wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(4026853731843413.4503)
,p_process_sequence=>10
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'GetColumnNames'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    v_dsType              varchar2(100);',
'    v_dsName              varchar2(1000);',
'    v_dsTable             varchar2(1000);',
'    v_sql                 varchar2(32767);',
'begin',
'    v_dsType := wwv_flow.g_x01;',
'    v_dsName := wwv_flow.g_x02;',
' ',
'    if v_dsType = ''SQL_QUERY'' then',
'        v_sql := wwv_flow.g_x03;',
'',
'        wwv_flow_json.open_object;',
'        wwv_flow_json.open_array(''columns'');',
'        for column_record in ( select column_name',
'                                 from table( wwv_flow_exec_dev.get_query_columns(',
'                                        p_location        => ''LOCAL'',',
'                                        p_query_type      => ''SQL'',',
'                                        p_sql_query       => v_sql,',
'                                        p_parse_as_schema => :P4100_SCHEMA',
'                                    )',
'                                )',
'        )',
'        loop',
'            wwv_flow_json.write( column_record.column_name );',
'        end loop;',
'        wwv_flow_json.close_array;',
'        wwv_flow_json.close_object;',
'    else',
'',
'        select table_name',
'          into v_dsTable ',
'          from apex_dg_bp_data_sources',
'         where name = v_dsName;',
'        ',
'        if v_dsTable is not null then',
'            wwv_flow_json.open_object;',
'            wwv_flow_json.open_array(''columns'');',
'            for dsColumn in ( select a.column_name col_name',
'                                from sys.dba_tab_columns a',
'                               where a.table_name = v_dsTable )',
'            loop',
'                wwv_flow_json.write( dsColumn.col_name );',
'            end loop;',
'            wwv_flow_json.close_array;',
'            wwv_flow_json.close_object;',
'        else',
'            wwv_flow_json.open_object;',
'            wwv_flow_json.write(''success'', false);',
'            wwv_flow_json.write(''message'', ''No table found on this schema for this custom data source: '' || v_dsName );',
'            wwv_flow_json.close_object;',
'        end if;',
'    end if;',
'exception',
'    when others then',
'        wwv_flow_json.open_object;',
'        wwv_flow_json.write(''success'', false);',
'        wwv_flow_json.write(''message'', sqlerrm);',
'        wwv_flow_json.close_object;',
'        raise;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>4026853731843413.4503
);
wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(4027112299843416.4503)
,p_process_sequence=>30
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'GetBPTablesAndColumns'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    sourceType         varchar2(3000);',
'    dataSourceCategory varchar2(3000);',
'    blueprintName      varchar2(1000);',
'begin',
'    wwv_flow_json.open_object; -- Open top level JSON object.',
'    ',
'    for bp in ( select name,',
'                       display_name,',
'                       description,',
'                       default_schema,',
'                       created_on,',
'                       created_by,',
'                       last_updated_on,',
'                       last_updated_by',
'                  from wwv_dg_blueprints',
'                 where id = :P4100_BLUEPRINT_ID )',
'    loop',
'        blueprintName := bp.name;',
'        wwv_flow_json.write( ''label'', bp.name );',
'        wwv_flow_json.write( ''display'', bp.display_name );',
'        wwv_flow_json.write( ''oldLabel'', bp.name );',
'        wwv_flow_json.write( ''type'', ''blueprint'' );',
'        wwv_flow_json.write( ''description'', bp.description );',
'        wwv_flow_json.write( ''schema'', bp.default_schema );',
'        wwv_flow_json.write( ''created'', bp.created_on );',
'        wwv_flow_json.write( ''createdBy'', bp.created_by );',
'        wwv_flow_json.write( ''updated'', bp.last_updated_on );',
'        wwv_flow_json.write( ''updatedBy'', bp.last_updated_by );',
'        wwv_flow_json.write( ''nodeAction'', ''none'' );',
'    end loop;',
'',
'    wwv_flow_json.open_array( ''children'' ); -- Open tables & data sources array.',
'    wwv_flow_json.open_object; -- Open table object.',
'    wwv_flow_json.write( ''label'', ''Tables'' );',
'    wwv_flow_json.write( ''type'', ''TablesParent'' );',
'    wwv_flow_json.open_array( ''children'' ); -- Open tables array',
'    ',
'    for bp_tbl in ( select id,',
'                           name,',
'                           seq,',
'                           display_name,',
'                           num_rows',
'                      from wwv_dg_blueprint_tables ',
'                     where blueprint_id = :P4100_BLUEPRINT_ID',
'                     order by seq )',
'    loop',
'        wwv_flow_json.open_object; -- Open table object',
'        wwv_flow_json.write( ''id'', bp_tbl.id );',
'        wwv_flow_json.write( ''type'', ''table'' );',
'        wwv_flow_json.write( ''label'', bp_tbl.name );',
'        wwv_flow_json.write( ''blueprintName'', blueprintName );',
'        wwv_flow_json.write( ''oldLabel'', bp_tbl.name );',
'        wwv_flow_json.write( ''display'', bp_tbl.display_name );',
'        wwv_flow_json.write( ''seq'', bp_tbl.seq );',
'        wwv_flow_json.write( ''rows'', bp_tbl.num_rows );',
'        wwv_flow_json.write( ''nodeAction'', ''none'' );',
'        -- Append columns:',
'        wwv_flow_json.open_array( ''children'' ); -- Open columns array',
'        for bp_tbl_col in ( select id,',
'                                   name,',
'                                   display_name,',
'                                   seq,',
'                                   lang,',
'                                   data_source_type,',
'                                   data_source,',
'                                   min_numeric_value,',
'                                   max_numeric_value,',
'                                   numeric_precision,',
'                                   min_date_value,',
'                                   max_date_value,',
'                                   data_format_mask,',
'                                   sequence_start_with,',
'                                   sequence_increment,',
'                                   data_source_table,',
'                                   data_source_column,',
'                                   depends_on,',
'                                   formula,',
'                                   percent_blank,',
'                                   native_datatype,',
'                                   multi_value_yn,',
'                                   mv_delimiter,',
'                                   mv_min_entries,',
'                                   mv_max_entries,',
'                                   mv_partition_by,',
'                                   mv_format,',
'                                   mv_unique,',
'                                   max_length',
'                     from wwv_dg_blueprint_tab_columns',
'                    where blueprint_table_id = bp_tbl.id',
'                    order by seq )',
'        loop',
'            -- Source types are transformed in the DG view:',
'            -- Need to transform it on each case, otherwise we pass the value as-is',
'            -- This is modeled in the treeview as it is in the original DG tables,',
'            -- but the use of the views is recommended by Insum.',
'            sourceType := upper( replace( bp_tbl_col.data_source_type, '' '', ''_'' ) );',
'',
'            wwv_flow_json.open_object; -- Open column object',
'',
'            wwv_flow_json.write( ''id'', bp_tbl_col.id );',
'            wwv_flow_json.write( ''type'', ''column'' || ''-'' || lower( sourceType ) );',
'            wwv_flow_json.write( ''label'', bp_tbl_col.name );',
'            wwv_flow_json.write( ''oldLabel'', bp_tbl_col.name );',
'            wwv_flow_json.write( ''parentTable'', bp_tbl.name );',
'            wwv_flow_json.write( ''blueprintName'', blueprintName );',
'            wwv_flow_json.write( ''display'', bp_tbl_col.display_name );',
'            wwv_flow_json.write( ''seq'', bp_tbl_col.seq );',
'            wwv_flow_json.write( ''lang'', bp_tbl_col.lang );',
'            wwv_flow_json.write( ''source'', sourceType );',
'            wwv_flow_json.write( ''ds'', bp_tbl_col.data_source );',
'            -- Add other key-values depending on data source type.',
'            if sourceType = ''BUILTIN'' then',
'                wwv_flow_json.write( ''builtin'', bp_tbl_col.data_source || '':'' || bp_tbl_col.native_datatype );',
'                wwv_flow_json.write( ''dsType'', '''' );',
'                wwv_flow_json.write( ''dsCustomDataSource'', '''' );',
'                wwv_flow_json.write( ''dsName'', '''' );',
'                wwv_flow_json.write( ''dsColumn'', '''' );',
'                wwv_flow_json.write( ''dsQuery'', '''' );               ',
'                wwv_flow_json.write( ''inline'', '''' );',
'            elsif sourceType = ''DATA_SOURCE'' then',
'                wwv_flow_json.write( ''dsCustomDataSource'', bp_tbl_col.data_source );',
'                -- Verify if the data source is a table or query.',
'                for ds_type in ( select name,',
'                                        data_source_type,',
'                                        sql_query',
'                                   from wwv_dg_blueprint_data_sources',
'                                  where name = substr( bp_tbl_col.data_source, 0, instr( bp_tbl_col.data_source, ''.'' ) - 1 ) )',
'                loop',
'                    wwv_flow_json.write( ''builtin'', '''' );',
'                    wwv_flow_json.write( ''inline'', '''' );',
'                    wwv_flow_json.write( ''dsName'', ds_type.name );',
'                    wwv_flow_json.write( ''dsColumn'', substr( bp_tbl_col.data_source, instr( bp_tbl_col.data_source, ''.'' ) + 1, length( bp_tbl_col.data_source ) ) );',
'                    wwv_flow_json.write( ''dsType'', ds_type.data_source_type );',
'                    if ds_type.data_source_type = ''TABLE'' then',
'                        wwv_flow_json.write( ''dsQuery'', '''' );',
'                    elsif ds_type.data_source_type = ''SQL_QUERY'' then',
'                        -- Required for fetching the DS columns later.',
'                        wwv_flow_json.write( ''dsQuery'', ds_type.sql_query );',
'                    end if;',
'                end loop;',
'            elsif sourceType = ''INLINE'' then',
'                wwv_flow_json.write( ''builtin'', '''' );',
'                wwv_flow_json.write( ''inline'', bp_tbl_col.data_source );',
'                wwv_flow_json.write( ''dsType'', '''' );',
'                wwv_flow_json.write( ''dsCustomDataSource'', '''' );',
'                wwv_flow_json.write( ''dsName'', '''' );',
'                wwv_flow_json.write( ''dsColumn'', '''' );',
'                wwv_flow_json.write( ''dsQuery'', '''' ); ',
'            end if;',
'',
'            wwv_flow_json.write( ''min'', bp_tbl_col.min_numeric_value );',
'            wwv_flow_json.write( ''max'', bp_tbl_col.max_numeric_value );',
'            wwv_flow_json.write( ''precision'', bp_tbl_col.numeric_precision );',
'            wwv_flow_json.write( ''minDate'', to_char( bp_tbl_col.min_date_value, bp_tbl_col.data_format_mask ) );',
'            wwv_flow_json.write( ''maxDate'', to_char( bp_tbl_col.max_date_value, bp_tbl_col.data_format_mask ) );',
'            wwv_flow_json.write( ''formatMask'', bp_tbl_col.data_format_mask );',
'            wwv_flow_json.write( ''seqStart'', bp_tbl_col.sequence_start_with );',
'            wwv_flow_json.write( ''seqIncrement'', bp_tbl_col.sequence_increment );',
'            wwv_flow_json.write( ''data_source_table'', bp_tbl_col.data_source_table ); -- These appear to be always null.',
'            wwv_flow_json.write( ''data_source_column'', bp_tbl_col.data_source_column ); -- These appear to be always null.',
'            wwv_flow_json.write( ''dependsOn'', bp_tbl_col.depends_on );',
'            wwv_flow_json.write( ''formula'', bp_tbl_col.formula );',
'            if bp_tbl_col.percent_blank = ''0'' then',
'                wwv_flow_json.write( ''required'', ''Y'' );',
'            else',
'                wwv_flow_json.write( ''required'', ''N'' );',
'            end if;',
'            wwv_flow_json.write( ''blank'', bp_tbl_col.percent_blank );',
'            wwv_flow_json.write( ''mv'', bp_tbl_col.multi_value_yn );',
'            wwv_flow_json.write( ''mvDelimiter'', bp_tbl_col.mv_delimiter );',
'            wwv_flow_json.write( ''mvMin'', bp_tbl_col.mv_min_entries );',
'            wwv_flow_json.write( ''mvMax'', bp_tbl_col.mv_max_entries );',
'            wwv_flow_json.write( ''mvPartition'', bp_tbl_col.mv_partition_by );',
'            wwv_flow_json.write( ''mvFormat'', bp_tbl_col.mv_format );',
'            wwv_flow_json.write( ''mvUnique'', bp_tbl_col.mv_unique );',
'            wwv_flow_json.write( ''maxLength'', bp_tbl_col.max_length );',
'            wwv_flow_json.write( ''nodeAction'', ''none'' );',
'            wwv_flow_json.close_object; -- Close column object',
'        end loop;',
'        wwv_flow_json.close_array; -- Close columns array',
'',
'        wwv_flow_json.close_object; -- Close table object',
'    end loop;',
'',
'    wwv_flow_json.close_array; -- Close tables array.',
'    wwv_flow_json.close_object; -- Close table object.',
'    wwv_flow_json.open_object; -- Open data sources object.',
'    wwv_flow_json.write( ''label'', ''Custom Data Sources'' );',
'    wwv_flow_json.write( ''type'', ''DataSourcesParent'' );',
'    wwv_flow_json.open_array( ''children'' ); -- Open data sources array',
'    for bp_ds in ( select id,',
'                          name,',
'                          data_source_type,',
'                          table_name,',
'                          sql_query,',
'                          where_clause ',
'                     from wwv_dg_blueprint_data_sources',
'                    where blueprint_id = :P4100_BLUEPRINT_ID )',
'    loop',
'        wwv_flow_json.open_object; -- Open data source object',
'        wwv_flow_json.write( ''id'',  bp_ds.id );',
'        wwv_flow_json.write( ''type'', ''datasource'' );',
'        wwv_flow_json.write( ''blueprintName'', blueprintName );',
'        wwv_flow_json.write( ''label'', bp_ds.name );',
'        wwv_flow_json.write( ''oldLabel'', bp_ds.name );',
'        wwv_flow_json.write( ''dsType'', bp_ds.data_source_type );',
'        wwv_flow_json.write( ''table'', bp_ds.table_name );',
'        wwv_flow_json.write( ''sqlQuery'', bp_ds.sql_query );',
'        wwv_flow_json.write( ''whereClause'', bp_ds.where_clause );',
'        wwv_flow_json.write( ''nodeAction'', ''none'' );',
'        wwv_flow_json.close_object; -- Close data source object',
'    end loop;',
'    wwv_flow_json.close_array; -- Close data sources array',
'    wwv_flow_json.close_object; -- Close data source object.',
'    wwv_flow_json.close_array; -- Close tables and data sources array.',
'    wwv_flow_json.close_object; -- Close top level JSON object.',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>4027112299843416.4503
);
wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(5402394591066021.4503)
,p_process_sequence=>40
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'SaveBlueprintObject'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_node_clob           clob;',
'    l_node_action         varchar2(1000);',
'    l_node_name           varchar2(1000);',
'    l_old_node_name       varchar2(1000);',
'    l_description         varchar2(32767);',
'    l_schema              varchar2(1000);',
'    l_parent_name         varchar2(1000);',
'    l_sequence            varchar2(1000);',
'    l_display_name        varchar2(1000);',
'    l_rows                varchar2(1000);',
'    l_max_length          varchar2(1000);',
'    l_mv                  varchar2(1000);',
'    l_mv_format           varchar2(1000);',
'    l_mv_unique           varchar2(1000);',
'    l_mv_delimiter        varchar2(1000);',
'    l_mv_min_entries      varchar2(1000);',
'    l_mv_max_entries      varchar2(1000);',
'    l_mv_partition_by     varchar2(1000);',
'    l_lang                varchar2(1000);',
'    l_data_source_type    varchar2(1000);',
'    l_data_source         varchar2(1000);',
'    l_return_type         varchar2(1000);',
'    l_min_numeric_value   varchar2(1000);',
'    l_max_numeric_value   varchar2(1000);',
'    l_numeric_precision   varchar2(1000);',
'    l_min_date_value      varchar2(1000);',
'    l_max_date_value      varchar2(1000);',
'    l_format_mask         varchar2(1000);',
'    l_sequence_start_with varchar2(1000);',
'    l_sequence_increment  varchar2(1000);',
'    l_formula             varchar2(32767);',
'    l_percent_blank       varchar2(1000);',
'    l_ds_type             varchar2(1000);',
'    l_table               varchar2(1000);',
'    l_sql_query           varchar2(32767);',
'    l_where_clause        varchar2(1000);',
'    l_returning_id        number;',
'    l_blueprint_name      varchar2(1000);',
'begin',
'    for i in 1..wwv_flow.g_f01.count loop',
'        l_node_clob := l_node_clob || wwv_flow.g_f01(i);',
'    end loop;',
'',
'    wwv_flow_json.parse(',
'        p_source => l_node_clob );',
'',
'    l_node_action := wwv_flow_json.get_varchar2( p_path=>''nodeAction'' );',
'    l_node_name := wwv_flow_json.get_varchar2( p_path=>''label'' );',
'    l_old_node_name := wwv_flow_json.get_varchar2( p_path=>''oldLabel'' );',
'    l_blueprint_name := wwv_flow_json.get_varchar2( p_path=>''blueprintName'' );',
