,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with messages as (',
'select',
'    wwv_flow_lang.system_message(''APEX.ISSUES.SELECT'') as label_select,',
'    wwv_flow_lang.system_message(''APEX.ISSUES.ASSIGN'') as label_assign,',
'    wwv_flow_lang.system_message(''APEX.ISSUES.REMOVE'') as label_remove,',
'    wwv_flow_lang.system_message(''CLOSED'') as milestone_closed,',
'    wwv_flow_lang.system_message(''PAST_DUE'') as milestone_past_due,',
'    wwv_flow_lang.system_message(''DUE'') as milestone_due',
'from dual ) ',
'select',
'  null as ITEM_CUSTOM_ATTRS,',
'  null as ITEM_CSS_MODIFIERS, ',
'  ''<label for="P1_M01_''||rownum||''">''||wwv_flow_escape.html(milestone_name)||''</label>'' as ITEM_TITLE,',
'  HTMLDB_ITEM.SELECT_LIST(',
'    p_idx           => 2,',
'    p_value         => null,',
'    p_list_values   => msg.label_assign||'';ASSIGN_''||id||'',''||msg.label_remove||'';REMOVE_''||id||'''',',
'    p_attributes    => ''class="a-Form-select apex-item-select"'',',
'    p_show_null     => ''YES'',',
'    p_null_value    => ''NONE'',',
'    p_null_text     => msg.label_select,',
'    p_item_id       => ''P1_M01_''||rownum,',
'    p_item_label    => NULL,',
'    p_show_extra    => ''NO'') ITEM_ACTIONS,',
'  case ',
'      when milestone_status = ''CLOSED'' then ''fa fa-check-square xfam-check xfam-is-success u-success-text''',
'      when milestone_status = ''OPEN'' then',
'        case ',
'             when sysdate > milestone_date  then ''fa fa-exclamation-circle xfam-warning xfam-is-danger u-danger-text''',
'             when sysdate + 15 > milestone_date then ''fa fa-warning xfam-warning xfam-is-warning u-warning-text''',
'             else ''fa fa-flag-o''',
'        end ',
'  end as ITEM_ICON,',
'  null as ITEM_LINK,',
'  case ',
'      when milestone_status = ''CLOSED'' then ''<span class="fa fa-check-square u-success-text" aria-hidden="true"></span> '' || milestone_closed || '' &mdash; '' || to_char(milestone_date, ''Month DD YYYY'')',
'      when milestone_status = ''OPEN'' then',
'        case ',
'             when sysdate > milestone_date  then ''<span class="fa fa-warning u-danger-text" aria-hidden="true"></span> '' || milestone_past_due || '' &mdash; '' || htmldb_util.get_since(milestone_date)',
'             when sysdate + 15 > milestone_date then ''<span aria-hidden="true" class="fa fa-info-square u-warning-text"></span> '' || milestone_due || '' &mdash; '' || htmldb_util.get_since(milestone_date)',
'             else ''<span aria-hidden="true" class="fa fa-calendar-o"></span> '' || milestone_due || '' &mdash; '' || htmldb_util.get_since(milestone_date)',
'        end ',
'  end as ITEM_DESCRIPTION,',
'',
'  -- htmldb_util.get_since(milestone_date) as ITEM_DESCRIPTION,',
'  null as ITEM_MISC,',
'  null as ITEM_SELECTION',
'  ',
'  from wwv_flow_milestones m, messages msg',
' where security_group_id = :flow_security_group_id',
' order by milestone_date asc',
''))
