,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_ts_path varchar2(512);',
'  l_omf     boolean := FALSE;',
'begin',
'    for c1 in (select null',
'                 from v$parameter',
'                where upper(name) = ''DB_CREATE_FILE_DEST''',
'                  and trim(value) is not null) loop',
'        -- OMF is potentially in use',
'        l_omf := TRUE;',
'        exit;',
'    end loop;',
'',
'    --',
'    -- Only compute the file path if OMF is not in use',
'    -- ',
'    if l_omf = FALSE and wwv_flow_instance_admin.get_parameter(''INSTANCE_TABLESPACE'') is null then',
'        select replace(f.file_name,''\'',''/'')',
'          into l_ts_path',
'          from sys.dba_data_files f, sys.dba_tables t',
'         where t.table_name = ''WWV_FLOWS''',
'           and t.tablespace_name = f.tablespace_name',
'           and rownum = 1;',
'',
'        <<stripfilename>>',
'        for i in reverse 1 .. length(l_ts_path) loop',
'            if substr(l_ts_path,i,1) = ''/'' then',
'                l_ts_path := substr(l_ts_path,1,i);',
'                exit stripfilename;',
'            end if;',
'        end loop;',
'        --',
'        return wwv_flow_escape.html(l_ts_path) || ''APEX_XXX.DBF'';',
'    elsif wwv_flow_instance_admin.get_parameter(''INSTANCE_TABLESPACE'') is not null and l_omf = FALSE then',
'        for c1 in (select replace(f.file_name,''\'',''/'') file_name',
'          from sys.dba_data_files f  ',
'         where f.tablespace_name = wwv_flow_instance_admin.get_parameter(''INSTANCE_TABLESPACE'') ',
'           and rownum=1) loop',
'             return wwv_flow_escape.html(c1.file_name);',
'         end loop;',
'    else',
'        return wwv_flow_lang.system_message(p_name=>''PROVISION_REQUEST.SYSTEM_ASSIGNED'');',
'    end if;',
'end;'))
,p_source_type=>'FUNCTION_BODY'
