,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with app_stats as (',
'    select count(case',
'                     when f.created_on between to_date(:P1000_START_INTERVAL, ''&DATE_FORMAT.'')',
'                                       and trunc(sysdate) - 1/24/3600',
'                     then f.id',
'                 end) as app_total_1x,',
'           count(f.id) as app_total',
'       from wwv_flows f',
'      where f.security_group_id not in (0, 10, 11, 12)',
'        and f.id not between 4000 and 4999',
'        and f.created_on <= trunc(sysdate) - 1/24/3600 )     ',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label       ',
' from (',
'    select 1                                                                    as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_TOTAL_APPLICATIONS'') as stats_category,',
'           null                                                                 as label_class,',
'           nvl(app_total, 0)                                                  as total,',
'           case when nvl(app_total_1x, 0) != 0',
'                then nvl(app_total, 0) - nvl(app_total_1x, 0)',
'                else 0',
'           end                                                                  as delta',
