'with workspace_schemas_stats_1x as (',
'    select log_day,',
'           security_group_id,',
'           sum(data_file_size) as total_space,',
'           sum(free_space) as free_space',
'      from wwv_schema_aggr_metrics  m,',
'           wwv_flow_company_schemas s',
'     where s.schema = m.schema ',
'       and s.security_group_id not in (0, 10, 11, 12)',
'       and log_day between to_date(:P1000_START_INTERVAL, ''&DATE_FORMAT.'') and trunc(sysdate) - 1/24/3600',
'  group by log_day,',
'           security_group_id ),',
'workspace_schemas_stats as (',
'    select log_day,',
'           security_group_id,',
'           sum(data_file_size) as total_space,',
'           sum(free_space) as free_space',
'      from wwv_schema_aggr_metrics  m,',
'           wwv_flow_company_schemas s',
'     where s.schema (+) = m.schema',
'       and s.security_group_id not in (0, 10, 11, 12)',
'       and log_day <= trunc(sysdate) - 1/24/3600',
'  group by log_day,',
'           security_group_id ),',
'workspace_schemas as (',
'    select count(distinct security_group_id) as wksp_cnt_free_space_1x,',
'           0 as wksp_cnt_free_space',
'      from workspace_schemas_stats_1x',
'     where (free_space / nullif(total_space, 0)) * 100 < :AI_WORKSPACE_FREE_SPACE_LIMIT',
'     union all',
'    select 0 as wksp_cnt_free_space_1x,',
'           count(distinct security_group_id) as wksp_cnt_free_space',
'      from workspace_schemas_stats )     ',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
