,p_region_template_options=>'#DEFAULT#:a-IRR-region--noOuterBorders'
,p_plug_template=>wwv_flow_imp.id(721125879764730304.4051)
,p_plug_display_sequence=>30
,p_include_in_reg_disp_sel_yn=>'Y'
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with time_frame as (',
'    select case',
'           when :P1021_SINCE < 100',
'           then trunc(sysdate) - nvl(:P1021_SINCE, :AI_DEFAULT_REPORTING_PERIOD)',
'           else to_date(:P1021_START_DATE)',
'           end start_interval_1x,',
'           case',
'           when :P1021_SINCE < 100',
'           then trunc(sysdate) - 1/24/3600',
'           else to_date(:P1021_END_DATE)',
'           end end_interval_1x',
'      from sys.dual ),',
'all_apps_1x as (',
'    select a.workspace_id,',
'           a.workspace_name,',
'           a.application_id,',
'           a.application_name,',
'           sum(a.page_events) as page_events_total,',
'           max(a.distinct_pages) as max_distinct_pages,',
'           max(a.distinct_users) as max_distinct_users,',
'           sum(a.distinct_sessions) as distinct_sessions_total,',
'           sum(a.error_count) as error_count_total                ',
'      from wwv_flow_log_history a,',
'           time_frame          tf',
'     where a.workspace_id not in (0, 10, 11, 12)',
'       and a.application_id not between 4000 and 4999',
'       and a.log_day between tf.start_interval_1x and tf.end_interval_1x',
'  group by a.workspace_id,',
'           a.workspace_name,',
'           a.application_id,',
'           a.application_name ),',
'all_apps as (',
'    select a.workspace_id,',
'           a.workspace_name,',
'           a.application_id,',
'           a.application_name,',
'           sum(a.page_events) as page_events_total,',
'           max(a.distinct_pages) as max_distinct_pages,',
'           max(a.distinct_users) as max_distinct_users,',
'           sum(a.distinct_sessions) as distinct_sessions_total,',
'           sum(a.error_count) as error_count_total                ',
'      from wwv_flow_log_history a',
'     where a.workspace_id not in (0, 10, 11, 12)',
'       and a.application_id not between 4000 and 4999',
'       and a.log_day <= trunc(sysdate) - 1',
'  group by a.workspace_id,',
'           a.workspace_name,',
'           a.application_id,',
'           a.application_name ),',
'    order_apps as (',
'        select row_number() over (order by b.page_events_total desc,',
'                                           b.max_distinct_pages desc,',
'                                           b.max_distinct_users desc,',
'                                           b.distinct_sessions_total desc,',
'                                           b.error_count_total desc) as app_order,',
'               b.workspace_id,',
'               b.workspace_name,',
'               b.application_name,',
'               b.page_events_total,',
'               trunc(nvl(b.page_events_total, 0)/nullif(d.page_events_total, 0) * 100, 2)             as pg_page_events,',
'               b.max_distinct_pages,',
'               trunc(nvl(b.max_distinct_pages, 0)/nullif(d.max_distinct_pages, 0) * 100, 2)           as pg_distinct_pages,',
'               b.max_distinct_users,',
'               trunc(nvl(b.max_distinct_users, 0)/nullif(d.max_distinct_users, 0) * 100, 2)           as pg_distinct_users,',
'               b.distinct_sessions_total,',
'               trunc(nvl(b.distinct_sessions_total, 0)/nullif(d.distinct_sessions_total, 0) * 100, 2) as pg_distinct_sessions,',
'               b.error_count_total,',
'               trunc(nvl(b.error_count_total, 0)/nullif(d.error_count_total, 0) * 100, 2)             as pg_error_count,',
'               (select count(s.id)',
'                  from wwv_flow_steps s',
'                 where s.flow_id = b.application_id',
'                   and ((:P1021_SINCE < 100 and s.created_on <= trunc(sysdate) - 1)',
'                    or (:P1021_SINCE = 100 and s.created_on <= :P1021_END_DATE))) as app_pages',
'          from all_apps_1x   b,',
'               all_apps      d',
'         where d.workspace_id = b.workspace_id',
'           and d.application_id = b.application_id',
'           and d.workspace_name is not null',
'           and d.application_name is not null',
'           and b.page_events_total < d.page_events_total )',
'        select app_order app_rank,',
'               workspace_id,           ',
'               workspace_name,',
'               application_name,',
'               page_events_total,',
'               pg_page_events,',
'               max_distinct_pages,',
'               pg_distinct_pages,',
'               max_distinct_users,',
'               pg_distinct_users,',
'               distinct_sessions_total,',
'               pg_distinct_sessions,               ',
'               error_count_total,',
'               pg_error_count,',
