,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with user_accounts_stats_1x as (',
'    select max(wm.admin_user_accounts_cnt) + max(wm.dev_user_accounts_cnt) + max(wm.end_user_accounts_cnt) as user_accounts_total,',
'           max(wm.admin_user_accounts_cnt)                                                                 as admin_user_accounts_cnt,',
'           max(wm.dev_user_accounts_cnt)                                                                   as dev_user_accounts_cnt,',
'           max(wm.end_user_accounts_cnt)                                                                   as end_user_accounts_cnt,',
'           wm.workspace_name',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.log_day between to_date(:P1029_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'')',
'  group by wm.workspace_name ),',
'user_accounts_stats_2x as (',
'    select max(wm.admin_user_accounts_cnt) + max(wm.dev_user_accounts_cnt) + max(wm.end_user_accounts_cnt) as user_accounts_total,',
'           max(wm.admin_user_accounts_cnt)                                                                 as admin_user_accounts_cnt,',
'           max(wm.dev_user_accounts_cnt)                                                                   as dev_user_accounts_cnt,',
'           max(wm.end_user_accounts_cnt)                                                                   as end_user_accounts_cnt,',
'           wm.workspace_name',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.log_day between to_date(:P1029_START_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'')',
'  group by wm.workspace_name )',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       to_char(round((delta / nullif(total,0)) * 100), ''999G999G999G999G999G999G990'') || case when total != 0 then ''%''',
'                                                                                         end as delta_pct,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label',
' from (',
'    select 1                                                                               as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_ADMIN_ACCOUNT'')                 as stats_category,',
'           null                                                                            as label_class,',
'           nvl(sum(b.admin_user_accounts_cnt), 0)                                          as total,',
'           nvl(sum(b.admin_user_accounts_cnt), 0) - nvl(sum(d.admin_user_accounts_cnt), 0) as delta',
'      from user_accounts_stats_1x b,',
'           user_accounts_stats_2x d',
'     where b.workspace_name = d.workspace_name (+)',
'    union all',
'    select 2                                                                           as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_DEV_ACCOUNT'')               as stats_category,',
'           null                                                                        as label_class,',
'           nvl(sum(b.dev_user_accounts_cnt), 0)                                        as total,',
'           nvl(sum(b.dev_user_accounts_cnt), 0) - nvl(sum(d.dev_user_accounts_cnt), 0) as delta',
'      from user_accounts_stats_1x b,',
'           user_accounts_stats_2x d',
'     where b.workspace_name = d.workspace_name (+)                ',
'    union all',
'    select 3                                                                           as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_ENDUSER_ACCOUNT'')           as stats_category,',
'           null                                                                        as label_class,',
'           nvl(sum(b.end_user_accounts_cnt), 0)                                        as total,',
'           nvl(sum(b.end_user_accounts_cnt), 0) - nvl(sum(d.end_user_accounts_cnt), 0) as delta',
'      from user_accounts_stats_1x b,',
'           user_accounts_stats_2x d',
'     where b.workspace_name = d.workspace_name (+) )',
'order by sort_order'))
,p_ajax_enabled=>'Y'
,p_ajax_items_to_submit=>'P1029_START_TIME_INTERVAL_1X,P1029_END_TIME_INTERVAL_1X,P1029_START_TIME_INTERVAL_2X,P1029_END_TIME_INTERVAL_2X'
