'with user_accounts_stats_1x as (',
'    select max(wm.admin_user_accounts_cnt) + max(wm.dev_user_accounts_cnt) + max(wm.end_user_accounts_cnt) as user_accounts_total,',
'           max(wm.admin_user_accounts_cnt)                                                                 as admin_user_accounts_cnt,',
'           max(wm.dev_user_accounts_cnt)                                                                   as dev_user_accounts_cnt,',
'           max(wm.end_user_accounts_cnt)                                                                   as end_user_accounts_cnt',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.workspace_id = :P1030_WORKSPACE_ID',
'       and wm.log_day between to_date(:P1030_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1030_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') ),',
'user_accounts_stats_2x as (',
'    select max(wm.admin_user_accounts_cnt) + max(wm.dev_user_accounts_cnt) + max(wm.end_user_accounts_cnt) as user_accounts_total,',
'           max(wm.admin_user_accounts_cnt)                                                                 as admin_user_accounts_cnt,',
'           max(wm.dev_user_accounts_cnt)                                                                   as dev_user_accounts_cnt,',
'           max(wm.end_user_accounts_cnt)                                                                   as end_user_accounts_cnt',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.workspace_id = :P1030_WORKSPACE_ID',
'       and wm.log_day between to_date(:P1030_START_TIME_INTERVAL_2X , ''&DATE_FORMAT.'') and to_date(:P1030_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') )',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       to_char(round((delta / nullif(total,0)) * 100), ''999G999G999G999G999G999G990'') || case when total != 0 then ''%''',
'                                                                                         end as delta_pct,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
