,p_component_template_options=>'#DEFAULT#:a-Report--inline:a-Report--staticRowColors:a-Report--horizontalBorders:a-Report--stretch'
,p_new_grid_row=>false
,p_item_display_point=>'BELOW'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with connection_attempts_stats_1x as (',
'    select sum(login_cnt - failed_logins_cnt) as successful_login_total,',
'           sum(failed_logins_cnt)             as failed_login_total',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.workspace_id = :P1030_WORKSPACE_ID',
'       and wm.log_day between to_date(:P1030_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1030_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') ),',
'connection_attempts_stats_2x as (',
'     select sum(login_cnt - failed_logins_cnt) as successful_login_total,',
'           sum(failed_logins_cnt)             as failed_login_total',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.workspace_id = :P1030_WORKSPACE_ID',
'       and wm.log_day between to_date(:P1030_START_TIME_INTERVAL_2X , ''&DATE_FORMAT.'') and to_date( :P1030_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') )',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       to_char(round((delta / nullif(total,0)) * 100), ''999G999G999G999G999G999G990'') || case when total != 0 then ''%''',
'                                                                                         end as delta_pct,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label',
' from (',
'    select 1                                                                   as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_SUCCESSLOGIN'')      as stats_category,',
'           null                                                                as label_class,',
'           nvl(b.successful_login_total, 0)                                    as total,',
'           nvl(b.successful_login_total, 0) - nvl(d.successful_login_total, 0) as delta',
'      from connection_attempts_stats_1x b,',
'           connection_attempts_stats_2x d',
'    union all',
'    select 2                                                              as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_FAILEDLOGINS'') as stats_category,',
'           null                                                           as label_class,',
'           nvl(b.failed_login_total, 0)                                   as total,',
'           nvl(b.failed_login_total, 0) - nvl(d.failed_login_total, 0)    as delta',
'      from connection_attempts_stats_1x b,',
'           connection_attempts_stats_2x d )',
'order by sort_order'))
,p_ajax_enabled=>'Y'
,p_ajax_items_to_submit=>'P1030_START_TIME_INTERVAL_1X,P1030_END_TIME_INTERVAL_1X,P1030_START_TIME_INTERVAL_2X,P1030_END_TIME_INTERVAL_2X,P1030_WORKSPACE_ID'
