'  l_workspace_id number;',
'begin',
'  for c1 in (select m.service_attribute_1',
'               from WWV_FLOW_PROVISION_SERICE_MOD m',
'              where id           = :P110_REQUEST_ID',
'                and service_name = ''NEW_SCHEMA'')',
'  loop',
