'    ',
'    if l_workspace_id is null then',
'        raise program_error;',
'    end if;',
'        ',
'    l_msg := l_msg || :P110_Comments || chr(10) || chr(10);',
'    l_msg := l_msg || wwv_flow_mail.get_instance_url(p_security_group_id => l_workspace_id) || chr(10);',
'    l_body_html := wwv_flow_mail.convert_txt_to_html(p_txt_message => l_msg,p_security_group_id => l_workspace_id);',
'',
'    if l_email is null then',
'        for c1 in ( select ',
