,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select display_auth.id,',
'       trunc(display_auth.id) base_id,',
'       display_auth.name,',
'       help_text description,',
'       case active_auth.scheme_type',
'         when display_auth.scheme_type then consts.is_current',
'         else consts.not_current',
'       end is_current',
'  from wwv_flow_authentications display_auth,',
'       ( select active_auth.id,',
'                active_auth.flow_id,',
'                active_auth.scheme_type',
'           from wwv_flow_authentications active_auth,',
'                wwv_flows f',
'          where f.security_group_id      = 10',
'            and f.id                     = 4050',
'            and active_auth.id                = f.authentication_id',
'            and active_auth.security_group_id = f.security_group_id ) active_auth,',
'       ( select wwv_flow_lang.system_message(''CURRENT'') is_current,',
'                wwv_flow_lang.system_message(''NOT_CURRENT'') not_current,',
'                nvl(wwv_flow_lang.get_translated_application_id, 4050) translated_flow_id',
'           from sys.dual ) consts',
' where display_auth.security_group_id = 10',
'   and display_auth.flow_id           = consts.translated_flow_id',
'   and trunc(display_auth.id)         <> active_auth.id',
'   and display_auth.scheme_type       not like ''%CLOUD_IDM''',
'   and ( display_auth.scheme_type not like ''%.SSO''',
'         or exists ( select null',
'                       from sys.user_objects',
'                      where object_type = ''PACKAGE''',
'                        and object_name = ''WWV_FLOW_CUSTOM_AUTH_SSO'' ))',
'   and ( display_auth.scheme_type not like ''%.SAML''',
'         or (select wwv_flow_public_key_util.is_supported_yn from sys.dual)=''Y'' )',
' order by display_auth.name'))
