'with time_frame as (',
'    select case',
'           when :P1021_SINCE < 100',
'           then trunc(sysdate) - nvl(:P1021_SINCE, :AI_DEFAULT_REPORTING_PERIOD)',
'           else to_date(:P1021_START_DATE)',
'           end start_interval_1x,',
'           case',
'           when :P1021_SINCE < 100',
'           then trunc(sysdate) - 1/24/3600',
'           else to_date(:P1021_END_DATE)',
'           end end_interval_1x',
'      from sys.dual ),',
'all_apps_1x as (',
'    select a.workspace_id,',
'           a.workspace_name,',
'           a.application_id,',
'           a.application_name,',
'           sum(a.page_events) as page_events_total,',
'           max(a.distinct_pages) as max_distinct_pages,',
'           max(a.distinct_users) as max_distinct_users,',
'           sum(a.distinct_sessions) as distinct_sessions_total,',
'           sum(a.error_count) as error_count_total                ',
'      from wwv_flow_log_history a,',
'           time_frame          tf',
'     where a.workspace_id not in (0, 10, 11, 12)',
'       and a.application_id not between 4000 and 4999',
'       and a.log_day between tf.start_interval_1x and tf.end_interval_1x',
'  group by a.workspace_id,',
'           a.workspace_name,',
'           a.application_id,',
'           a.application_name ),',
'all_apps as (',
'    select a.workspace_id,',
'           a.workspace_name,',
'           a.application_id,',
'           a.application_name,',
'           sum(a.page_events) as page_events_total,',
'           max(a.distinct_pages) as max_distinct_pages,',
'           max(a.distinct_users) as max_distinct_users,',
'           sum(a.distinct_sessions) as distinct_sessions_total,',
'           sum(a.error_count) as error_count_total                ',
'      from wwv_flow_log_history a',
'     where a.workspace_id not in (0, 10, 11, 12)',
'       and a.application_id not between 4000 and 4999',
'       and a.log_day <= trunc(sysdate) - 1',
'  group by a.workspace_id,',
'           a.workspace_name,',
'           a.application_id,',
'           a.application_name ),',
'    order_apps as (',
'        select row_number() over (order by b.page_events_total desc,',
