'               b.page_events_total,',
'               trunc(nvl(b.page_events_total, 0)/nullif(d.page_events_total, 0) * 100, 2)             as pg_page_events,',
'               b.max_distinct_pages,',
'               trunc(nvl(b.max_distinct_pages, 0)/nullif(d.max_distinct_pages, 0) * 100, 2)           as pg_distinct_pages,',
'               b.max_distinct_users,',
'               trunc(nvl(b.max_distinct_users, 0)/nullif(d.max_distinct_users, 0) * 100, 2)           as pg_distinct_users,',
'               b.distinct_sessions_total,',
'               trunc(nvl(b.distinct_sessions_total, 0)/nullif(d.distinct_sessions_total, 0) * 100, 2) as pg_distinct_sessions,',
'               b.error_count_total,',
'               trunc(nvl(b.error_count_total, 0)/nullif(d.error_count_total, 0) * 100, 2)             as pg_error_count,',
'               (select count(s.id)',
'                  from wwv_flow_steps s',
'                 where s.flow_id = b.application_id',
'                   and ((:P1021_SINCE < 100 and s.created_on <= trunc(sysdate) - 1)',
'                    or (:P1021_SINCE = 100 and s.created_on <= :P1021_END_DATE))) as app_pages',
'          from all_apps_1x   b,',
'               all_apps      d',
'         where d.workspace_id = b.workspace_id',
'           and d.application_id = b.application_id',
'           and d.workspace_name is not null',
'           and d.application_name is not null',
'           and b.page_events_total < d.page_events_total )',
'        select app_order app_rank,',
'               workspace_id,           ',
'               workspace_name,',
'               application_name,',
'               page_events_total,',
'               pg_page_events,',
'               max_distinct_pages,',
'               pg_distinct_pages,',
'               max_distinct_users,',
'               pg_distinct_users,',
'               distinct_sessions_total,',
