,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'list contents only'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  c_browser_language  constant varchar2(255)   := wwv_flow.g_browser_language;',
'  c_nls_numeric_chars constant varchar2(8)     := wwv_flow.g_nls_decimal_separator || wwv_flow.g_nls_group_separator;',
'  l_list                       varchar2(32767);',
'  l_size                       varchar2(255);',
'',
'  l_file_names                 wwv_flow_global.vc_arr2;',
'  l_file_status                wwv_flow_global.vc_arr2;',
'  l_file_has_error             boolean := false;',
'',
'begin',
'  wwv_imp_util.set_list_contents_only( true );',
'  wwv_flow_imp_parser.parse ( p_file_id => :P115_FILE_ID );',
'  -- Restore NLS settings since parsing of an export file has likely changed it',
'  wwv_flow.g_browser_language := c_browser_language;',
'',
'  sys.dbms_session.set_nls (',
'      param => ''NLS_NUMERIC_CHARACTERS'',',
'      value => '''''''' || replace(c_nls_numeric_chars, '''''''', '''''''''''') || '''''''' );',
'',
'  wwv_flow.reset_g_nls_decimal_separator ();',
'',
'  wwv_imp_workspace.get_imported_files_and_status(',
'      p_file_names => l_file_names,',
'      p_file_status => l_file_status );',
'',
'  for i in 1..l_file_names.count loop',
'       if l_file_status(i) like ''%SCRIPT_EXCEEDS_MAX'' then',
'           l_size := substr(l_file_status(i),',
'                     1,',
'                     instr(l_file_status(i), ''_'', 1) - 1',
'                     );',
'           l_file_has_error := true;',
'       end if;',
'       ',
'       if i = 1 then',
'           l_list := ''<table class="a-Report  a-Report--stretch" cellspacing="0" cellpadding="0" border="0" aria-label="''||wwv_flow_lang.system_message(''F4500_P115.SCRIPTS_TO_INSTALL'')||''">'';',
'           l_list := l_list||''<tr><th scope="col" class="a-Report-colHead">''||wwv_flow_lang.system_message(''F4500_P115.SCRIPTS_TO_INSTALL'');',
'           l_list := l_list||''</td><th scope="col" class="a-Report-colHead">''||wwv_flow_lang.system_message(''F4000_P801_ACTION'')||''</td></tr>'';',
'       end if;',
'',
'       l_list := l_list||''<tr>'';',
'       l_list := l_list||''<td class="a-Report-cell" scope="row">''||wwv_flow_escape.html(l_file_names(i))||''</td>'';',
'       l_list := l_list||''<td class="a-Report-cell" scope="row">''||',
'                 ( case when l_file_status(i) like ''%SCRIPT_EXCEEDS_MAX'' then',
'                     wwv_flow_lang.system_message(''WWV_FLOW_API.SCRIPT_EXCEEDS_MAX'', l_size)',
'                 when l_file_status(i) = ''REPLACE'' then',
'                     wwv_flow_lang.system_message(''WWV_FLOW_API.REPLACE_SCRIPT_FILE'')',
'                 when l_file_status(i) = ''NEW'' then',
'                     wwv_flow_lang.system_message(''WWV_FLOW_API.NEW_SCRIPT_FILE'')',
'                 end )',
'                 ||''</td>'';',
'       l_list := l_list||''</tr>'';',
'       if i = l_file_names.count then',
'           l_list := l_list||''</table>'';',
'       end if;',
'  end loop;',
'',
'  if l_file_has_error then',
'      :P115_INSTALL := ''N'';',
'  else',
'      :P115_INSTALL := ''Y'';',
'  end if;',
'  :P115_SCRIPTS_TO_IMPORT := l_list;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_process_error_message=>'Unable to list script names.'
,p_process_when=>'P115_COMPATIBLE'
,p_process_when_type=>'VAL_OF_ITEM_IN_COND_EQ_COND2'
