'                            wwv_flow_escape.html(',
'                            case when length(sql_text) > decode(:P542_COLUMNS,''LESS'',125,75)',
'                             then substr(replace(sql_text,'','','', ''),1,decode(:P542_COLUMNS,''LESS'',125,75))||''...''',
'                             else replace(sql_text,'','','', '')',
'                            end) ||',
'                       ''</a>''',
'                else wwv_flow_escape.html(',
'                     case when length(sql_text) > decode(:P542_COLUMNS,''LESS'',125,75)',
'                     then substr(replace(sql_text,'','','', ''),1,decode(:P542_COLUMNS,''LESS'',125,75))||''...''',
'                     else replace(sql_text,'','','', '')',
'                     end )',
