'         23,''VAL INDEX'', ',
'         24,''CRE PROC'', ',
'         25,''ALT PROC'', ',
'         26,''LOCK TABLE'', ',
'         28,''RENAME'', ',
'         29,''COMMENT'', ',
'         30,''AUDIT'', ',
'         31,''NOAUDIT'', ',
'         32,''CRE DBLINK'', ',
'         33,''DROP DBLINK'', ',
'         34,''CRE DB'', ',
'         35,''ALTER DB'', ',
'         36,''CRE RBS'', ',
'         37,''ALT RBS'', ',
'         38,''DROP RBS'', ',
'         39,''CRE TBLSPC'', ',
'         40,''ALT TBLSPC'', ',
'         41,''DROP TBLSPC'', ',
'         42,''ALT SESSION'', ',
'         43,''ALT USER'', ',
'         44,''COMMIT'', ',
'         45,''ROLLBACK'', ',
'         46,''SAVEPOINT'', ',
'         47,''PL/SQL EXEC'', ',
'         48,''SET XACTN'', ',
