'         69,''DRP PROC'', ',
'         70,''ALT RESOURCE'', ',
'         71,''CRE SNPLOG'', ',
'         72,''ALT SNPLOG'', ',
'         73,''DROP SNPLOG'', ',
'         74,''CREATE SNAP'', ',
'         75,''ALT SNAP'', ',
