'         98,''ALT PKG BODY'', ',
'         99,''DRP PKG BODY'', ',
'         to_char(s.command)) command',
'  from waiters_blockers wb, sys.gv_$session s,',
'       lock_objects o',
' where s.sid = wb.waiting_session',
'   and s.inst_id = :P550_INST_ID',
'   and o.session_id(+) = wb.waiting_session',
'   and o.inst_id = s.inst_id',
'  ),',
'ws_schemas as(',
'select schema ',
'  from wwv_flow_company_schemas',
' where security_group_id = :flow_security_group_id)',
'select case when holding_session is null then ''<img src="#IMAGE_PREFIX#htmldb/sessions/locks.gif" />'' ',
'            else ''<span style="padding-left:''||power(2*level,2)||''px;"''||',
'                 ''><img src="#IMAGE_PREFIX#htmldb/sessions/lock_wait.gif" /></span>''',
'       end status_icon,',
'       waiting_session,',
'       username,',
'       case when lock_obj_count = 1 then',
'            case when owner in (select schema from ws_schemas) then',
'                ''<a href="''||wwv_flow_page_api.get_url(',
'                                 p_page        => 2000,',
'                                 p_clear_cache => ''RP,2000'',',
'                                 p_items       => ''P2000_OB_INIT_SCHEMA,P2000_OB_INIT_ID'',',
'                                 p_values      => owner||'',''||object_id )||''">''||wwv_flow_escape.html(object_name)||''</a>''',
'            else wwv_flow_escape.html(object_name) end',
'        else ''<br />'' end object_name,      ',
'       object_id,',
'       owner,',
'       object_type',
'  from wait_block_ses ',
' start with holding_session is null',
' connect by prior waiting_session =  holding_session'))
,p_customized=>'1'
