,p_process_name=>'print report'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_sql               varchar2(32767);',
'    l_bind_names        sys.dbms_sql.varchar2_table;',
'    l_bind_values       wwv_flow_global.vc_arr2;',
'    l_parameters        wwv_flow_exec_api.t_parameters;',
'',
'    l_csv_charset       varchar2(255) := NULL;',
'    l_context           wwv_flow_exec.t_context;',
'    l_run               wwv_flow_data_export.t_run;',
'    l_csv               clob;',
'    l_blob              blob;',
'        ',
'begin',
'',
'    wwv_flow.g_browser_language := :P1204_BROWSER_LANG;',
'    wwv_flow_lang.alter_session(wwv_flow_lang.map_language(wwv_flow.g_browser_language));',
'    ',
'    wwv_flow_init_htp_buffer;',
'    wwv_flow.g_page_text_generated := true;',
'    wwv_flow.g_unrecoverable_error := true;',
'    ',
'    wwv_flow_utilities.print_download_header(',
'        p_mime_type     => ''text/plain'',',
'        p_file_name     => ''report.csv'',',
'        p_is_attachment => true );',
'  ',
'    sys.owa_util.http_header_close;',
'',
'    if wwv_flow_preferences.get_preference( p_preference => ''F4500_CSV_DOWNLOAD_UNICODE'' ) = ''Y'' then',
'      l_csv_charset := ''AL32UTF8'';',
'    else',
'      for c1 in (',
'          select csv_encoding, flow_language_derived_from',
'          from wwv_flows',
'          where id = wwv_flow.g_flow_id',
'      ) loop',
'          if c1.flow_language_derived_from <> ''0'' and nvl(c1.csv_encoding,''N'') = ''Y'' then',
'              l_csv_charset := wwv_flow_lang.get_nls_windows_charset;',
'          end if;',
'          exit;',
'      end loop;',
'    end if;',
'    ',
'    -- get the statement to run',
'    l_sql := :P1003_SQL_COMMAND2;',
'',
'    l_bind_names := wwv_flow_utilities.get_binds( l_sql );',
'',
'    -- get the binds values used last time if they exist',
'    l_bind_values := wwv_flow_utilities.string_to_table (',
'        str => :P1200_BIND_VALS,',
'        sep => '','');',
'',
'    for i in 1..l_bind_names.count loop',
'        if l_bind_values(i) != ''HTMLDB:NULL'' then',
'            wwv_flow_exec_api.add_parameter(',
'                p_parameters    => l_parameters,',
'                p_name          => l_bind_names(i),',
'                p_value         => l_bind_values(i) );',
'        end if;',
'    end loop;',
'',
'    wwv_flow_exec.open_query_context(',
'        p_context                   => l_context,',
'        p_location                  => wwv_flow_exec_api.c_location_local_db,',
'        p_query_type                => wwv_flow_exec_api.c_query_type_sql_query,',
'        p_sql_query                 => :P1003_SQL_COMMAND2,',
'        p_sql_parameters            => l_parameters,',
'        p_parse_as_schema_override  => :P1003_SCHEMA );',
'        ',
'    l_run := wwv_flow_data_export.run(',
'        p_context   => l_context,',
'        p_format    => wwv_flow_data_export_api.c_format_csv );',
'        ',
'    l_csv := wwv_flow_data_export.get_clob_output( p_run => l_run );',
'',
'    wwv_flow_exec.close_context( l_context );',
'',
'    if (l_csv_charset is not null) then',
'        ',
'        l_blob := wwv_flow_utilities.clob_to_blob( p_clob => l_csv, p_charset => l_csv_charset );',
'        ',
'        -- download the binary contents of the BLOB',
'        sys.wpg_docload.download_file( l_blob ); ',
'    ',
'        -- cleanup the temporary BLOB ',
'        sys.dbms_lob.freetemporary( l_blob );   ',
'        ',
'    else',
'        -- print the results',
'        htmldb_util.prn( l_csv );',
'',
'    end if;',
'',
'    -- cleanup the temporary CLOB     ',
'    sys.dbms_lob.freetemporary( l_csv );',
'    ',
'    wwv_flow.g_unrecoverable_error := true;',
'',
'exception',
'    when others then',
'        wwv_flow_exec.close_context( l_context );',
'        wwv_flow.g_unrecoverable_error := true;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_process_error_message=>'error'
,p_process_success_message=>'ok'
,p_internal_uid=>204028208695700079
