'            p_c008            => :COLUMN_COMMENTS,',
'            p_n001            => :SORT_ORDER );',
'    when ''D'' then',
'        wwv_flow_collection.delete_member(',
'            p_collection_name => l_collection_name,',
'            p_seq             => :SEQ_ID );',
