wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(6801180864730230)
,p_process_sequence=>30
,p_process_point=>'ON_SUBMIT_BEFORE_COMPUTATION'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Populate PK_SOURCE in TBL_WIZ_COLUMNS'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_seq_id number;',
'    l_collection_name varchar2(20) := ''TBL_WIZ_COLUMNS'';',
'begin',
'    -- this process is executed only when 1 column is selected as PK column',
'    -- so this query must execute with out any exception',
'    select seq_id',
'      into l_seq_id',
'      from wwv_flow_collections',
'     where collection_name = l_collection_name',
'       and c007            = ''Y''; ',
'',
'    -- for PK column, set c009 value as IDENTITY',
'    -- we need this info to add default value for PK column',
'    wwv_flow_collection.update_member_attribute (',
'        p_collection_name => l_collection_name,',
'        p_seq             => l_seq_id,',
'        p_attr_number     => 9,',
'        p_attr_value      => ''IDENTITY'' );     ',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select *',
'  from sys.dual',
' where (',
