,p_action_sequence=>30
,p_execute_on_page_init=>'Y'
,p_action=>'NATIVE_EXECUTE_PLSQL_CODE'
,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_profile             clob;',
'    l_file_profile        wwv_flow_data_parser.t_file_profile;',
'    l_column_name_changed boolean := false;',
'    l_column_map          wwv_flow_global.vc_map := wwv_flow_global.c_empty_vc_map;',
'begin',
'    select clob001 into l_profile ',
'    from wwv_flow_collections ',
'    where collection_name =''APEX$DATALOAD''',
'    and rownum = 1;',
'',
'    l_file_profile := wwv_flow_data_parser.json_to_profile( p_json => l_profile );',
'    if :P3100_FIRST_LINE is null or :P3100_FIRST_LINE != ''HEADERS'' then',
'        for i in 1 .. l_file_profile.file_columns.count loop',
'            l_file_profile.file_columns( i ).name := ''COL'' || lpad( i,3,''0'');',
'        end loop;',
'        l_profile := wwv_flow_data_parser.profile_to_json( l_file_profile );',
'        ',
'        l_column_name_changed := true;',
'        ',
'        -- see if configure columns are same, if not column names changed',
'        for i in 1 .. l_file_profile.file_columns.count',
'        loop',
'            for l_configure in (',
'                select',
'                    c001   column_name,',
'                    c002   mapped_column',
'                from wwv_flow_collections',
'                where collection_name = ''APEX$DATALOAD_COLUMNS''',
'                and c001 = l_file_profile.file_columns( i ).name )',
'            loop    ',
'                l_column_name_changed := false;',
'                exit;',
'            end loop;',
'        end loop;',
'    end if;',
'    ',
'    -- save changed file profile for later use',
'    wwv_flow_collection.create_or_truncate_collection( ''APEX$DATALOAD'' );',
'    wwv_flow_collection.add_member( ''APEX$DATALOAD'', p_clob001 => l_profile );',
'    ',
'    if l_column_name_changed then',
'        -- clear column mapping configuration as column names changed',
'        wwv_flow_collection.create_or_truncate_collection( p_collection_name => ''APEX$DATALOAD_COLUMNS'' );',
'    end if;',
'    ',
'    -- auto discover column mapping or get mapping user configured',
'    if :P3100_LOAD_TO = ''EXISTING'' then',
'        -- get column mapping user configured',
'        for l_configure in (',
'            select',
'                c001   column_name,',
'                c002   mapped_column',
'            from wwv_flow_collections',
'            where collection_name = ''APEX$DATALOAD_COLUMNS''',
'            and c002 is not null )',
'        loop',
'            l_column_map( l_configure.column_name ) := l_configure.mapped_column;',
'        end loop;',
'        ',
'        if l_column_map.count = 0 then',
'            -- get column mapping with existing table',
'            for i in 1 .. l_file_profile.file_columns.count',
'            loop',
'                for l_existing_table in (',
'                    select column_name',
'                    from sys.dba_tab_columns',
'                    where owner = :P3100_TABLE_OWNER',
'                    and table_name = :P3100_EXISTING_TABLE',
'                    and column_name = l_file_profile.file_columns( i ).name )',
'                loop',
'                    l_column_map( l_file_profile.file_columns( i ).name ) := l_existing_table.column_name;',
'                    -- add APEX$DATALOAD_COLUMNS collection as it is not populated yet',
'                    wwv_flow_collection.add_member(',
'                        p_collection_name => ''APEX$DATALOAD_COLUMNS'',',
'                        p_c001 => l_existing_table.column_name,                 -- column name',
'                        p_c002 => l_existing_table.column_name,                 -- mapped column',
'                        p_c003 => l_file_profile.file_columns( i ).format_mask, -- format mask',
'                        p_c004 => l_file_profile.file_columns( i ).group_char,  -- group char',
'                        p_c005 => l_file_profile.file_columns( i ).decimal_char -- decimal char    ',
'                        );',
'                end loop;',
'            end loop;',
'        end if;',
'    end if;',
'    -- need to compute here for configure region display',
'    :P3100_FILE_COLUMN_COUNT   := l_file_profile.file_columns.count;',
'    :P3100_MAPPED_COLUMN_COUNT := l_column_map.count;',
'end;'))
,p_attribute_02=>'P3100_FIRST_LINE,P3100_TABLE_OWNER,P3100_EXISTING_TABLE'
,p_attribute_03=>'P3100_MAPPED_COLUMN_COUNT,P3100_FILE_COLUMN_COUNT'
,p_attribute_04=>'N'
,p_attribute_05=>'PLSQL'
,p_wait_for_result=>'Y'
);
