wwv_flow_imp_page.create_page_plug(
 p_id=>wwv_flow_imp.id(18663279696871922)
,p_plug_name=>'get file profile and save'
,p_region_name=>'getFileProfile'
,p_parent_plug_id=>wwv_flow_imp.id(33362689698963696)
,p_component_template_options=>'#DEFAULT#'
,p_plug_display_sequence=>50
,p_plug_display_point=>'SUB_REGIONS'
,p_location=>null
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    c_columns_collection  constant varchar2(21) := ''APEX$DATALOAD_COLUMNS'';',
'    --',
'    l_profile                      clob;',
'    l_profile_record               wwv_flow_data_parser.t_file_profile;',
'    l_collection_columns           wwv_flow_global.t_bag;',
'    l_clear_columns_collection     boolean := false;',
'begin',
'    l_profile        := wwv_flow_data_parser.get_file_profile;',
'    l_profile_record := wwv_flow_data_parser.json_to_profile( l_profile );',
'',
'    if :P3120_FIRST_LINE is null or :P3120_FIRST_LINE != ''HEADERS'' then',
'        for i in 1 .. l_profile_record.file_columns.count loop',
'            l_profile_record.file_columns( i ).name := ''COL'' || lpad( i,3,''0'');',
'        end loop;',
'        l_profile := wwv_flow_data_parser.profile_to_json(l_profile_record);',
'        ',
'        -- if first line does not contain headers anymore,',
'        -- clear the columns collection',
'        l_clear_columns_collection := true;',
'       ',
'        -- for new table load, column name gets saved as c002',
'        -- for existing table load, column name gets saved as c001',
'        for l_configure in (',
'            select case when coalesce( :P3100_LOAD_TO, ''NEW'') = ''NEW'' then',
'                       c002',
'                   else ',
'                       c001',
'                   end column_name',
'              from wwv_flow_collections',
'             where collection_name = c_columns_collection )',
'        loop',
'            l_collection_columns( l_configure.column_name ) := null;',
'        end loop;',
'',
'        -- see if profile column exists in the configured columns collection',
'        -- if yes, do not clear the columns collection',
'        for i in 1 .. l_profile_record.file_columns.count loop',
'            if l_collection_columns.exists( l_profile_record.file_columns( i ).name ) then',
'                l_clear_columns_collection := false;',
'                exit;',
'            end if;',
'        end loop;',
'    end if;',
'',
'    -- save file profile in collection for later use',
'    wwv_flow_collection.create_or_truncate_collection( ''APEX$DATALOAD'' );',
'    wwv_flow_collection.add_member( ''APEX$DATALOAD'', p_clob001 => l_profile );',
'   ',
'    if l_clear_columns_collection then',
'        wwv_flow_collection.create_or_truncate_collection( p_collection_name => c_columns_collection );',
'    end if;',
'   ',
'    :P3120_PARSED_ROW_COUNT := l_profile_record.parsed_rows;',
'    ',
'    if l_profile_record.file_columns.count <= 100 then',
'        :P3120_PARSED_COLUMN_COUNT := l_profile_record.file_columns.count;',
'    else',
'        :P3120_PARSED_COLUMN_COUNT := 100;',
'    end if;',
'end;'))
,p_plug_source_type=>'NATIVE_PLSQL'
,p_translate_title=>'N'
);
wwv_flow_imp_page.create_report_region(
 p_id=>wwv_flow_imp.id(193340845691147579)
,p_name=>'Column Mapping'
,p_region_name=>'tab_columns'
,p_template=>wwv_flow_imp.id(385770331595976651)
,p_display_sequence=>80
,p_include_in_reg_disp_sel_yn=>'Y'
,p_region_css_classes=>'a-DataLoad-previewColumns'
,p_region_template_options=>'#DEFAULT#'
,p_component_template_options=>'#DEFAULT#:a-Report--staticRowColors:a-Report--horizontalBorders:a-Report--stretch'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with',
'l_msg as (',
'    select wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.UNMAPPED'') unmapped',
'    from sys.dual)',
', l_columns as (',
'    select ',
'        fc.column_position,    ',
'        case when :P3120_FIRST_LINE = ''HEADERS'' then',
'            fc.column_name',
'        else ',
'            ''COL'' || lpad( rownum,3,''0'')',
'        end as column_name,',
'        case when :P3120_FIRST_LINE = ''HEADERS'' then',
'            ( select tc.column_name ',
'              from sys.dba_tab_columns tc',
'              where tc.owner = :P3100_TABLE_OWNER',
'              and tc.table_name = :P3100_EXISTING_TABLE',
'              and tc.column_name = fc.column_name )',
'        else',
'             null',
'        end as map_column_name, ',
'        fc.data_type, ',
'        fc.format_mask,',
'        fc.detected_group_char group_char,',
'        fc.detected_decimal_char decimal_char',
'    from wwv_flow_collections c,',
'         table( wwv_flow_data_parser.get_columns( c.clob001 ) ) fc',
'    where c.collection_name =''APEX$DATALOAD'' )',
', l_sum_existing_tab_col as (',
'    select sum( length( ''<option value="''|| column_name ||''">''|| column_name ||'' (''|| data_type ||'')</option>'' ) ) total_length',
'    from sys.dba_tab_columns tc',
'    where tc.owner = :P3100_TABLE_OWNER',
'    and tc.table_name = :P3100_EXISTING_TABLE )',
'select',
'    c.column_position,',
'    c.column_name,',
'    c.data_type,',
'    htmldb_item.hidden(',
'        p_idx            => 1,',
'        p_value          => c.column_name)||',
'    case when tc.total_length <= 3900 then',
'        htmldb_item.select_list_from_lov(',
'            p_idx            => 2,',
'            p_value          => m.c002,',
'            p_lov            => ''P3120_TABLE_COLUMNS'',',
'            p_null_value     => null,',
'            p_null_text      => msg.unmapped,         ',
'            p_show_extra     => ''NO'',',
'            p_attributes     => ''aria-label="''||wwv_flow_lang.system_message(''DATA_WORKSHOP.CONFIGURE.MAP_TO_COLUMN'', wwv_flow_escape.html_attribute(c.column_name))||''"'') ',
'    else',
'        -- bug 30464422 fix, show popup if total existing table column option  > 3900 to avoid error',
'        wwv_flow_item.popup_from_lov(',
'            p_idx            => 2,',
'            p_value          => m.c002,',
'            p_lov_name       => ''P3120_TABLE_COLUMNS'',',
'            p_width          => 40,',
'            p_show_null      => ''YES'',',
