,p_process_name=>'New Table - Save Column Load Checkbox'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_profile           clob;',
'    l_col_selected      boolean := false;',
'    l_mapped_col_cnt    pls_integer := 0;',
'begin',
'    wwv_flow_collection.create_or_truncate_collection( p_collection_name => ''APEX$DATALOAD_COLUMNS'' );',
'    -- loop through all column postions',
'    for i in 1 .. wwv_flow.g_f06.count',
'    loop',
'        ',
'        for j in 1 .. wwv_flow.g_f01.count',
'        loop',
'            -- if column checked to load, save the column configuration',
'            if wwv_flow.g_f01( j ) = wwv_flow.g_f06( i ) then',
'',
'                wwv_flow_collection.add_member(',
'                    p_collection_name => ''APEX$DATALOAD_COLUMNS'',',
'                    p_c001            => wwv_flow.g_f06( i ), -- column position',
'                    p_c002            => wwv_flow.g_f02( i ), -- column name',
'                    p_c003            => wwv_flow.g_f03( i ), -- format mask',
'                    p_c004            => wwv_flow.g_f04( i ), -- group char',
'                    p_c005            => wwv_flow.g_f05( i ), -- decimal char',
'                    p_c006            => wwv_flow.g_f07( i ), -- column type',
'                    p_c007            => case when wwv_flow.g_f07( i ) = ''VARCHAR2'' then wwv_flow.g_f08( i ) end  -- max length',
'                    );',
'',
'                exit;',
'            end if;',
'        end loop;',
'    end loop;',
'        ',
'    select clob001',
'    into l_profile ',
'    from wwv_flow_collections ',
'    where collection_name = ''APEX$DATALOAD'' ',
'    and rownum = 1;',
'    ',
'    for i in (',
'        select column_position, column_name',
'        from table( wwv_flow_data_parser.get_columns( l_profile ) ) c ) ',
'    loop',
'        if wwv_flow_collection.collection_member_count( p_collection_name => ''APEX$DATALOAD_COLUMNS'' ) = 0 then',
'            l_col_selected := true;',
'        else',
'            l_col_selected := false;',
'            for l_select_col in (',
'                select c001 idx ',
'                from wwv_flow_collections',
'                where collection_name = ''APEX$DATALOAD_COLUMNS'' )',
'            loop',
'                begin',
'                    if to_number( l_select_col.idx ) = i.column_position then',
'                        l_col_selected := true;',
'                        exit;',
'                    end if;',
'                exception when invalid_number then null; ',
'                end;',
'            end loop;',
'        end if;',
'        ',
'        if l_col_selected then',
'            l_mapped_col_cnt := l_mapped_col_cnt + 1;',
'        end if;',
'    end loop;',
'    ',
'    :P3100_MAPPED_COLUMN_COUNT := l_mapped_col_cnt;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(3942483089766704)
