'declare',
'    l_column_map    wwv_flow_global.vc_map := wwv_flow_global.c_empty_vc_map;',
'begin    ',
'    wwv_flow_collection.create_or_truncate_collection( p_collection_name => ''APEX$DATALOAD_COLUMNS'' );',
'    wwv_flow_collection.add_members(',
'        p_collection_name => ''APEX$DATALOAD_COLUMNS'',',
'        p_c001            => wwv_flow.g_f01,  -- column_name',
'        p_c002            => wwv_flow.g_f02,  -- mapped column',
'        p_c003            => wwv_flow.g_f03,  -- format mask',
'        p_c004            => wwv_flow.g_f04,  -- group char',
'        p_c005            => wwv_flow.g_f05   -- decimal char           ',
'        );',
'    ',
'    -- get column mapping user configured',
'    for l_configure in (',
'        select',
'            c001   column_name,',
'            c002   mapped_column',
'        from wwv_flow_collections',
'        where collection_name = ''APEX$DATALOAD_COLUMNS''',
'        and c002 is not null )',
'    loop',
'        l_column_map( l_configure.column_name ) := l_configure.mapped_column;',
'    end loop;',
'',
'    --',
'    -- if no column has been mapped, clear the collection, so that APEX can automatically',
'    -- map columns again when "save" changes is clicked. This prevents columns from being',
'    -- doubled in the collection (bug #32405712)',
'    --',
'    if l_column_map.count = 0 then',
'        wwv_flow_collection.truncate_collection( p_collection_name => ''APEX$DATALOAD_COLUMNS'' );',
'    end if;         ',
'    ',
'    :P3100_MAPPED_COLUMN_COUNT := l_column_map.count;',
'',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(3942483089766704)
