,p_process_name=>'GetDbObjects'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_names           wwv_flow_dynamic_exec.vc_arr2;',
'    l_values          wwv_flow_dynamic_exec.vc_arr2;',
'    l_sql_statement   varchar2( 32767 );',
'    l_include_public  boolean;',
'    l_cursor          number := sys.dbms_sql.open_cursor;',
'    l_object_name     varchar2( 32767 );',
'    l_comment         varchar2( 32676 );',
'    l_ignore          number;',
'begin',
'',
'    l_include_public := ( wwv_flow.g_x03 = ''Y'' );',
'    l_names(1)  := ''p_schema_name'';',
'    l_values(1) := :DATAGEN_SCHEMA;',
'    l_names(2)  := ''p_type'';',
'    l_values(2) := wwv_flow.g_x01;',
'',
'    l_sql_statement := q''{',
'            select o.name,',
'                   c.comments',
'              from ( select object_name as name,',
'                            owner       as table_owner,',
'                            object_name as table_name',
'                       from sys.all_objects',
'                      where owner       = :p_schema_name',
'                        and object_type = :p_type',
'                      union',
'                     /* get all objects which are referenced by a private or public synonym */',
'                     select s.synonym_name as name,',
'                            s.table_owner,',
'                            s.table_name',
'                       from sys.all_synonyms s,',
'                            sys.all_objects o',
'                      where }'' ||',
'                        case',
'                          when l_include_public then q''{ s.owner in ( :p_schema_name, ''PUBLIC'' ) }''',
'                          else ''s.owner = :p_schema_name''',
'                        end || q''{',
'                        and o.owner       = s.table_owner',
'                        and o.owner      <> ''SYS''',
'                        and o.object_name = s.table_name',
'                        and o.object_type = :p_type',
'                        /* don''t include a public synonym if it points to a local object with the same name */',
'                        and not (   o.owner       = :p_schema_name',
'                                and o.object_name = s.synonym_name',
'                                )',
'                   ) o,',
'                   sys.all_tab_comments c',
'             where c.owner      = o.table_owner',
'               and c.table_name = o.table_name',
'               and c.table_type = :p_type',
'             order by 1',
'           }'';',
'',
'    wwv_flow_dynamic_exec.parse_sql_statement (',
'            p_cursor          => l_cursor,',
'            p_sql_statement   => l_sql_statement,',
'            p_auto_bind_items => false,',
'            p_names           => l_names,',
'            p_values          => l_values,',
'            p_parse_as_schema => :DATAGEN_SCHEMA );',
'',
'',
'    sys.dbms_sql.define_column( l_cursor, 1, l_object_name, 32767 );',
'    sys.dbms_sql.define_column( l_cursor, 2, l_comment, 32767 );',
'    l_ignore := sys.dbms_sql.execute( l_cursor );',
'    ',
'    -- Fetch as long as there is a row returned',
'    wwv_flow_json.open_array;',
'    while sys.dbms_sql.fetch_rows( l_cursor ) > 0',
'    loop',
'        sys.dbms_sql.column_value( l_cursor, 1, l_object_name );',
'        sys.dbms_sql.column_value( l_cursor, 2, l_comment );',
'',
'        wwv_flow_json.open_object;',
'        wwv_flow_json.  write( ''name'',    l_object_name );',
'        wwv_flow_json.  write( ''comment'', l_comment );',
'        wwv_flow_json.close_object;',
'    end loop;',
'    wwv_flow_json.close_array;',
'',
'    sys.dbms_sql.close_cursor(l_cursor);',
'',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_internal_uid=>2610228796483411
);
