,p_process_name=>'set values'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_site_account_lifetime number := ',
'    nvl(wwv_flow_platform.get_preference(''ACCOUNT_LIFETIME_DAYS''),45);',
'  l_workspace_account_lifetime number;',
'  l_account_last_updated date;',
'  l_dev_admin_expired boolean := false;',
'  l_end_user_expired boolean := false;',
'  l_found boolean := false;',
'begin',
'',
'--',
'-- account expiration notices',
'--',
'l_account_last_updated := nvl(:P23_ACCOUNT_EXPIRY, trunc(sysdate));',
'for c1 in (select ACCOUNT_LIFETIME_DAYS from WWV_FLOW_COMPANIES',
'           where provisioning_company_id = :F4050_P23_SECURITY_GROUP_ID) loop',
'  l_workspace_account_lifetime := c1.account_lifetime_days;',
'end loop;',
'',
':P23_SHOW_EXPIRE_FLAG := ''N'';',
'if :P23_DEVELOPER = ''Y'' then',
'  if l_account_last_updated + l_site_account_lifetime < sysdate then',
'    l_dev_admin_expired := true;',
'    :P23_EXPIRATION_STATUS1 := wwv_flow_lang.system_message(''EXPIRED'');',
'  else',
'    :P23_EXPIRATION_STATUS1 := wwv_flow_lang.system_message(''VALID'');',
'    :P23_SHOW_EXPIRE_FLAG := ''Y'';',
'  end if;',
'else',
'  :P23_EXPIRATION_STATUS1 := ''NA'';',
'end if;',
'',
'for c1 in (select ACCOUNT_LIFETIME_DAYS from WWV_FLOW_COMPANIES',
'           where EXPIRE_FND_USER_ACCOUNTS = ''Y''',
'           and provisioning_company_id = :F4050_P23_SECURITY_GROUP_ID) loop',
'  l_found := true;',
'  if l_account_last_updated + l_workspace_account_lifetime < sysdate then',
'    l_end_user_expired := true;',
'    :P23_EXPIRATION_STATUS2 := wwv_flow_lang.system_message(''EXPIRED'');',
'  else',
'    :P23_EXPIRATION_STATUS2 := wwv_flow_lang.system_message(''VALID'');',
'    :P23_SHOW_EXPIRE_FLAG := ''Y'';',
'  end if;',
'end loop;',
'if not l_found then',
'  :P23_EXPIRATION_STATUS2 := ''NA'';',
'end if;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>68692203366535952.4058
);
