'(',
'select',
'  h.log_day,',
'  sum(page_events) page_events',
'from   WWV_FLOW_LOG_HISTORY h',
'where  h.log_day >= sysdate-NVL(:P138_SINCE,7)',
'group by h.log_day',
'),',
'rolling30 as',
'(',
'select',
'  log_day,',
'  sum(page_events) over ( order by log_day range between 30 preceding and current row) as last_30_days',
