'declare',
'    l_schema_name         varchar2(256);',
'    l_p1                  varchar2(256)  := wwv_flow_provision.get_random_password(16);',
'    l_username_asserted   varchar2(4000) := wwv_flow_assert.enquote_name(upper(:P142_ADMIN));',
