'declare',
'  l_done boolean := false;',
'  l_company_cnt pls_integer := 0;',
'  l_workspace_name varchar2(255) :=  upper(wwv_flow_imp_parser.get_workspace_name(:P277_FILE_ID));',
'begin',
'  for c1 in (select count(*) cnt',
'             from wwv_flow_companies',
'             where provisioning_company_id = :P277_IMP_COMPANY_ID',
'             or short_name = l_workspace_name)',
'  loop',
'    l_company_cnt := c1.cnt;',
'  end loop;',
'  ',
'  if l_company_cnt = 0 then',
'    :P165_COMPANY_EXISTS := wwv_flow_lang.system_message(''F4000.NO'');',
'    :P165_COMPANY_ACTION := wwv_flow_lang.system_message(''F4050.WS_TOBE_CREATED'');',
'    :P165_WS_EXISTS := ''N'';',
'  else  ',
'      :P165_COMPANY_EXISTS := wwv_flow_lang.system_message(''F4000.YES'');  ',
'      :P165_WS_EXISTS := ''Y'';',
'      for c1 in (select 1',
'                 from wwv_flow_companies',
'                 where provisioning_company_id = :P277_IMP_COMPANY_ID',
'                 and short_name = l_workspace_name)',
'      loop',
'        l_done := true;',
'        :P165_COMPANY_ACTION := wwv_flow_lang.system_message(''F4050.WS_EXISTS'');',
'      end loop;',
'      ',
'      if not l_done then',
'          for c1 in (select 1',
'                     from wwv_flow_companies',
'                     where provisioning_company_id != :P277_IMP_COMPANY_ID',
'                     and short_name = l_workspace_name)',
'          loop',
'            l_done := true;',
'            :P165_COMPANY_ACTION := wwv_flow_lang.system_message(''F4050.WSNAME_EXISTS'');',
'          end loop;',
'          ',
'          if not l_done then',
'              for c1 in (select 1',
'                         from wwv_flow_companies',
'                         where provisioning_company_id = :P277_IMP_COMPANY_ID',
'                         and short_name != l_workspace_name)',
'              loop',
'                :P165_COMPANY_ACTION := wwv_flow_lang.system_message(''F4050.WSID_EXISTS'');',
'              end loop; ',
'          end if;',
'      end if;   ',
'  end if;',
'end;'))
