'         group by security_group_id) pv,',
'       -- developer page views',
'       (select security_group_id,count(*) d_page_views',
'          from wwv_flow_activity_log',
'         where flow_id between 4000 and 4999',
'         group by security_group_id) dv',
' where s.security_group_id = c.provisioning_company_id',
