,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with wksp_users_stats as (',
'    select count(case',
'                     when wu.creation_date between to_date(:P1000_START_INTERVAL, ''&DATE_FORMAT.'')',
'                                           and trunc(sysdate) - 1/24/3600',
'                     then wu.user_id',
'                 end) as wksp_users_total_1x,',
'           count(wu.user_id) as wksp_users_total      ',
'      from wwv_flow_workspace_users wu,',
'           wwv_flow_companies ws',
'     where ws.provisioning_company_id = wu.workspace_id',
'       and ws.provisioning_company_id not in (0, 10, 11, 12)',
'       and wu.creation_date <= trunc(sysdate) - 1/24/3600 )     ',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label       ',
' from (',
'    select 1                                                                       as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_TOTAL_WORKSPACE_USERS'') as stats_category,',
'           null                                                                    as label_class,',
