,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with dblinks_stats as (',
'    select sum(case',
'                   when db.created between to_date(:P1000_START_INTERVAL, ''&DATE_FORMAT.'')',
'                                   and trunc(sysdate) - 1/24/3600',
'                   then 1',
'                   else 0',
'               end) as db_links_cnt_1x,',
'           count(*) as db_links_cnt    ',
'      from sys.dba_db_links db',
'     where db.owner not in (''ORDS_METADATA'', ''SYS'', sys_context(''USERENV'',''CURRENT_USER''))',
'       and db.owner not in (select username',
'                              from sys.dba_users',
'                             where oracle_maintained = ''Y'')     ',
'       and db.created <= trunc(sysdate) - 1/24/3600 )     ',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label       ',
' from (',
'    select 1                                                         as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_DBLINKS'') as stats_category,',
'           null                                                      as label_class,',
'           nvl(db_links_cnt, 0)                                      as total,',
'           case when nvl(db_links_cnt_1x, 0) != 0',
'                then nvl(db_links_cnt, 0) - nvl(db_links_cnt_1x, 0)',
'                else 0',
'           end                                                       as delta',
'      from dblinks_stats )'))
